INSERT INTO setting VALUES("83","12","Time Zone Offset","timeoffset","+1","Time (in hours) that the server is offset from GMT. Please select the most appropriate option.","<select name=\\\"setting[$setting[settingid]]\\\">\r\n<option value=\\\"-12\\\" \".iif($setting[value]==-12,\"selected\",\"\").\">(GMT -12:00 hours) Eniwetok, Kwajalein</option>\r\n<option value=\\\"-11\\\" \".iif($setting[value]==-11,\"selected\",\"\").\">(GMT -11:00 hours) Midway Island, Samoa</option>\r\n<option value=\\\"-10\\\" \".iif($setting[value]==-10,\"selected\",\"\").\">(GMT -10:00 hours) Hawaii</option>\r\n<option value=\\\"-9\\\" \".iif($setting[value]==-9,\"selected\",\"\").\">(GMT -9:00 hours) Alaska</option>\r\n<option value=\\\"-8\\\" \".iif($setting[value]==-8,\"selected\",\"\").\">(GMT -8:00 hours) Pacific Time (US & Canada)</option>\r\n<option value=\\\"-7\\\" \".iif($setting[value]==-7,\"selected\",\"\").\">(GMT -7:00 hours) Mountain Time (US & Canada)</option>\r\n<option value=\\\"-6\\\" \".iif($setting[value]==-6,\"selected\",\"\").\">(GMT -6:00 hours) Central Time (US & Canada), Mexico City</option>\r\n<option value=\\\"-5\\\" \".iif($setting[value]==-5,\"selected\",\"\").\">(GMT -5:00 hours) Eastern Time (US & Canada), Bogota, Lima, Quito</option>\r\n<option value=\\\"-4\\\" \".iif($setting[value]==-4,\"selected\",\"\").\">(GMT -4:00 hours) Atlantic Time (Canada), Caracas, La Paz</option>\r\n<option value=\\\"-3.5\\\" \".iif($setting[value]==-3.5,\"selected\",\"\").\">(GMT -3:30 hours) Newfoundland</option>\r\n<option value=\\\"-3\\\" \".iif($setting[value]==-3,\"selected\",\"\").\">(GMT -3:00 hours) Brazil, Buenos Aires, Georgetown</option>\r\n<option value=\\\"-2\\\" \".iif($setting[value]==-2,\"selected\",\"\").\">(GMT -2:00 hours) Mid-Atlantic</option>\r\n<option value=\\\"-1\\\" \".iif($setting[value]==-1,\"selected\",\"\").\">(GMT -1:00 hours) Azores, Cape Verde Islands</option>\r\n<option value=\\\"0\\\" \".iif($setting[value]==0,\"selected\",\"\").\">(GMT) Western Europe Time, London, Lisbon, Casablanca, Monrovia</option>\r\n<option value=\\\"+1\\\" \".iif($setting[value]==+1,\"selected\",\"\").\">(GMT +1:00 hours) CET(Central Europe Time), Angola, Libya</option>\r\n<option value=\\\"+2\\\" \".iif($setting[value]==+2,\"selected\",\"\").\">(GMT +2:00 hours) EET(Eastern Europe Time), Kaliningrad, South Africa</option>\r\n<option value=\\\"+3\\\" \".iif($setting[value]==+3,\"selected\",\"\").\">(GMT +3:00 hours) Baghdad, Kuwait, Riyadh, Moscow, St. Petersburg, Volgograd, Nairobi</option>\r\n<option value=\\\"+3.5\\\" \".iif($setting[value]==+3.5,\"selected\",\"\").\">(GMT +3:30 hours) Tehran</option>\r\n<option value=\\\"+4\\\" \".iif($setting[value]==+4,\"selected\",\"\").\">(GMT +4:00 hours) Abu Dhabi, Muscat, Baku, Tbilisi</option>\r\n<option value=\\\"+4.5\\\" \".iif($setting[value]==+4.5,\"selected\",\"\").\">(GMT +4:30 hours) Kabul</option>\r\n<option value=\\\"+5\\\" \".iif($setting[value]==+5,\"selected\",\"\").\">(GMT +5:00 hours) Ekaterinburg, Islamabad, Karachi, Tashkent</option>\r\n<option value=\\\"+5.5\\\" \".iif($setting[value]==+5.5,\"selected\",\"\").\">(GMT +5:30 hours) Bombay, Calcutta, Madras, New Delhi</option>\r\n<option value=\\\"+6\\\" \".iif($setting[value]==+6,\"selected\",\"\").\">(GMT +6:00 hours) Almaty, Dhaka, Colombo</option>\r\n<option value=\\\"+7\\\" \".iif($setting[value]==+7,\"selected\",\"\").\">(GMT +7:00 hours) Bangkok, Hanoi, Jakarta</option>\r\n<option value=\\\"+8\\\" \".iif($setting[value]==+8,\"selected\",\"\").\">(GMT +8:00 hours) Beijing, Perth, Singapore, Hong Kong, Chongqing, Urumqi, Taipei</option>\r\n<option value=\\\"+9\\\" \".iif($setting[value]==+9,\"selected\",\"\").\">(GMT +9:00 hours) Tokyo, Seoul, Osaka, Sapporo, Yakutsk</option>\r\n<option value=\\\"+9.5\\\" \".iif($setting[value]==+9.5,\"selected\",\"\").\">(GMT +9:30 hours) Adelaide, Darwin</option>\r\n<option value=\\\"+10\\\" \".iif($setting[value]==+10,\"selected\",\"\").\">(GMT +10:00 hours) EAST(East Australian St