INSERT INTO setting VALUES("127","22","Spell Check Language","spellchecklang","ge","The language used for the spell checking features, provided by <a href=\"http://www.spellchecker.net/\" target=_blank>Spellchecker.net</a>. If you do not want to use these features, you should remove them from the template.","<select name=\\\"setting[$setting[settingid]]\\\">\r\n<option value=\\\"en\\\" \".iif($setting[value]==\"en\",\"selected\",\"\").\">American English</option>\r\n<option value=\\\"uk\\\" \".iif($setting[value]==\"uk\",\"selected\",\"\").\">British English</option>\r\n<option value=\\\"fr\\\" \".iif($setting[value]==\"fr\",\"selected\",\"\").\">French</option>\r\n<option value=\\\"ge\\\" \".iif($setting[value]==\"ge\",\"selected\",\"\").\">German</option>\r\n<option value=\\\"it\\\" \".iif($setting[value]==\"it\",\"selected\",\"\").\">Italian</option>\r\n<option value=\\\"sp\\\" \".iif($setting[value]==\"sp\",\"selected\",\"\").\">Spanish</option>\r\n<option value=\\\"dk\\\" \".iif($setting[value]==\"dk\",\"selected\",\"\").\">Danish</option>\r\n<option value=\\\"br\\\" \".iif($setting[value]==\"br\",\"selected\",\"\").\">Brazilian Portuguese</option>\r\n<option value=\\\"nl\\\" \".iif($setting[value]==\"nl\",\"selected\",\"\").\">Dutch</option>\r\n<option value=\\\"no\\\" \".iif($setting[value]==\"no\",\"selected\",\"\").\">Norwegian</option>\r\n<option value=\\\"pt\\\" \".iif($setting[value]==\"pt\",\"selected\",\"\").\">Portuguese</option>\r\n<option value=\\\"se\\\" \".iif($setting[value]==\"se\",\"selected\",\"\").\">Swedish</option>\r\n<option value=\\\"fi\\\" \".iif($setting[value]==\"fi\",\"selected\",\"\").\">Finnish</option>\r\n</select>","1");
