INSERT INTO template VALUES("27","-1","calendar_enterevent","{htmldoctype}\n<html>\n<head>\n<title>$bbtitle - Enter new Events</title>\n$headinclude\n</head>\n<body>\n$header\n\n<table cellpadding=\"2\" cellspacing=\"0\" border=\"0\" width=\"{contenttablewidth}\" {tableinvisibleextra} align=\"center\">\n<tr>\n\t<td width=\"100%\">$navbar</td>\n</tr>\n</table>\n\n<form action=\"calendar.php\" method=\"post\" name=\"vbform\">\n<input type=\"hidden\" name=\"s\" value=\"$session[sessionhash]\">\n\n<table cellpadding=\"{tableouterborderwidth}\" cellspacing=\"0\" border=\"0\" bgcolor=\"{tablebordercolor}\" {tableouterextra} width=\"{contenttablewidth}\" align=\"center\"><tr><td>\n<table cellpadding=\"4\" cellspacing=\"{tableinnerborderwidth}\" border=\"0\" {tableinnerextra} width=\"100%\">\n<tr>\n\t<td bgcolor=\"{tableheadbgcolor}\" colspan=\"2\"><normalfont color=\"{tableheadtextcolor}\" class=\"thtcolor\"><b>New Event</b></normalfont></td>\n</tr>\n$deleteevent\n$logincode\n<tr>\n\t<td bgcolor=\"{secondaltcolor}\" valign=\"top\"><normalfont><b>Date:</b></normalfont></td>\n\t<td bgcolor=\"{secondaltcolor}\">\n\t<table border=\"0\" cellspacing=\"0\" cellpadding=\"2\">\n\t<tr align=\"center\">\n\t\t<td><smallfont>Month</smallfont></td>\n\t\t<td><smallfont>Day</smallfont></td>\n\t\t<td><smallfont>Year</smallfont></td>\n\t</tr>\n\t<tr>\n\t\t<td><smallfont><select name=\"month\" tabindex=\"1\">\n\t\t\t<option value=\"1\" $month1selected>January</option>\n\t\t\t<option value=\"2\" $month2selected>February</option>\n\t\t\t<option value=\"3\" $month3selected>March</option>\n\t\t\t<option value=\"4\" $month4selected>April</option>\n\t\t\t<option value=\"5\" $month5selected>May</option>\n\t\t\t<option value=\"6\" $month6selected>June</option>\n\t\t\t<option value=\"7\" $month7selected>July</option>\n\t\t\t<option value=\"8\" $month8selected>August</option>\n\t\t\t<option value=\"9\" $month9selected>September</option>\n\t\t\t<option value=\"10\" $month10selected>October</option>\n\t\t\t<option value=\"11\" $month11selected>November</option>\n\t\t\t<option value=\"12\" $month12selected>December</option>\n\t\t</select></smallfont></td>\n\t\t<td><smallfont><select name=\"day\" tabindex=\"2\">\n\t\t\t<option value=\"1\" $day1selected>1</option>\n\t\t\t<option value=\"2\" $day2selected>2</option>\n\t\t\t<option value=\"3\" $day3selected>3</option>\n\t\t\t<option value=\"4\" $day4selected>4</option>\n\t\t\t<option value=\"5\" $day5selected>5</option>\n\t\t\t<option value=\"6\" $day6selected>6</option>\n\t\t\t<option value=\"7\" $day7selected>7</option>\n\t\t\t<option value=\"8\" $day8selected>8</option>\n\t\t\t<option value=\"9\" $day9selected>9</option>\n\t\t\t<option value=\"10\" $day10selected>10</option>\n\t\t\t<option value=\"11\" $day11selected>11</option>\n\t\t\t<option value=\"12\" $day12selected>12</option>\n\t\t\t<option value=\"13\" $day13selected>13</option>\n\t\t\t<option value=\"14\" $day14selected>14</option>\n\t\t\t<option value=\"15\" $day15selected>15</option>\n\t\t\t<option value=\"16\" $day16selected>16</option>\n\t\t\t<option value=\"17\" $day17selected>17</option>\n\t\t\t<option value=\"18\" $day18selected>18</option>\n\t\t\t<option value=\"19\" $day19selected>19</option>\n\t\t\t<option value=\"20\" $day20selected>20</option>\n\t\t\t<option value=\"21\" $day21selected>21</option>\n\t\t\t<option value=\"22\" $day22selected>22</option>\n\t\t\t<option value=\"23\" $day23selected>23</option>\n\t\t\t<option value=\"24\" $day24selected>24</option>\n\t\t\t<option value=\"25\" $day25selected>25</option>\n\t\t\t<option value=\"26\" $day26selected>26</option>\n\t\t\t<option value=\"27\" $day27selected>27</option>\n\t\t\t<option value=\"28\" $day28selected>28</option>\n\t\t\t<option value=\"29\" $day29selected>29</option>\n\t\t\t<option value=\"30\" $day30selected>30</option>\n\t\t\t<option value=\"31\" $day31selected>31</option>\n\t\t</select></smallfont></td>\n\t\t<td><smallfont><select name=\"year\" tabindex=\"3\">\n\t\t\t<option value=\"2000\" $year2000selected>2000</option>\n\t\t\t<option value=\"2001\" $year2001selected>2001</option>\n\t\t\t<option value=\"2002\" $year2002selected>2002</option>\n\t\t\t<option value=\"2003\" $year2003selected>2003</option>\n\t\t\t<option value=\"2004\" $year2004selected>2004</option>\n\t\t</select></smallfont></td>\n\t</tr>\n\t</table>\n\t</td>\n</tr>\n<tr>\n\t<td bgcolor=\"{firstaltcolor}\" valign=\"top\" nowrap><normalfont><b>Subject:</b></normalfont></td>\n\t<td bgcolor=\"{firstaltcolor}\"><input type=\"text\" class=\"bginput\" name=\"subject\" value=\"$subject\" size=\"50\" maxlength=\"50\" tabindex=\"4\"></td>\n</tr>\n$vbcode_buttons\n<tr>\n\t<td bgcolor=\"{secondaltcolor}\" valign=\"top\" nowrap><p><normalfont><b>Event Information:</b></normalfont></p>\t\t\t$vbcode_smilies</td>\n\t<td bgcolor=\"{secondaltcolor}\">\n\t\t<table cellpadding=\"0\" cellspacing=\"0\" border=\"0\">\n\t\t<tr valign=\"top\">\n\t\t\t<td><textarea name=\"message\" rows=\"20\" cols=\"$textareacols\" wrap=\"virtual\" tabindex=\"5\">$message</textarea></td>\n\t\t</tr>\n\t\t</table>\n\t</td>\n</tr>\n<tr>\n\t<td bgcolor=\"{firstaltcolor}\" valign=\"top\"><normalfont><b>Options</b></normalfont></td>\n\t<td bgcolor=\"{firstaltcolor}\"><smallfont>\n\t\t<input type=\"checkbox\" name=\"parseurl\" value=\"yes\" $parseurlchecked>\n\t\t<b>Automatically parse URLs:</b> automatically adds [url] and [/url] around internet addresses.\n\t\t$disablesmiliesoption\n\t\t$convert\n\t</smallfont></td>\n</tr>\n</table>\n</td></tr></table>\n\n<br>\n\n<table cellpadding=\"2\" cellspacing=\"0\" border=\"0\" width=\"{contenttablewidth}\" {tableinvisibleextra} align=\"center\">\n<tr>\n\t<td align=\"center\"><smallfont>\n\t<b>Posts:</b> HTML Code is $htmlcodeon.\n\t<a href=\"misc.php?s=$session[sessionhash]&action=showsmilies\">Smilies</a> are $smilieson.\n\t<a href=\"misc.php?s=$session[sessionhash]&action=bbcode\">vB Code</a> is $bbcodeon. [IMG] code is $imgcodeon.\n\t<br><br>\n\t<input type=\"submit\" class=\"bginput\" value=\"Submit Event\" accesskey=\"s\" tabindex=\"6\">&nbsp;&nbsp;&nbsp;\n\t<!-- Don\'t bother trying to manipulate this form to post public events if you don\'t have access to. You won\'t be successful -->\n\t<input type=\"reset\" class=\"bginput\" value=\"Clear Fields\" tabindex=\"7\">\n\t<input type=\"hidden\" name=\"eventid\" value=\"$eventid\">\n\t<input type=\"hidden\" name=\"action\" value=\"update\">\n\t<input type=\"hidden\" name=\"type\" value=\"$type\">\n\t</smallfont></td>\n</tr>\n</table>\n\n</form>\n\n$footer\n \n</body>\n</html>");
