INSERT INTO template VALUES("37","-1","coppaform","{htmldoctype}\n<html>\n<head>\n<title>Permission to Participate at $bbtitle</title>\n</head>\n\n<body bgcolor=\"#ffffff\">\n\n<table border=\"0\" width=\"100%\">\n<tr>\n\t<td valign=\"top\" align=\"left\"><a href=\"index.php?s=$session[sessionhash]\"><img src=\"{titleimage}\" border=\"0\"></a></td>\n\t<td valign=\"middle\" align=\"center\" nowrap>$toplinks</td>\n</tr>\n</table>\n\n<center><normalfont><b>Permission to Participate at $bbtitle</b></normalfont></center>\n\n<normalfont>\n\t<p><b>Instructions for a Parent or Guardian</b></p>\n\t\n\t<p>Please print out this page, sign it and fax it to:<br>\n\t$companyname at $faxnumber</p>\n\t\n\t<p>or mail it to :<br>\n\t$address</p>\n\t\n\t<p>If approval is not received in 7 days the information will be deleted.</p>\n</normalfont>\n\n<table width=\"100%\" border=\"1\" cellpadding=\"4\" cellspacing=\"1\">\n<tr>\n\t<td colspan=\"2\"><p><normalfont><b>Required Information</b></normalfont></p></td>\n</tr>\n<tr>\n\t<td width=\"20%\"><normalfont><b>User Name:</b></normalfont></td>\n\t<td width=\"80%\"><normalfont><b>$bbuserinfo[username]&nbsp;</b></normalfont></td>\n</tr>\n<tr>\n\t<td width=\"20%\"><normalfont><b>Password:</b></normalfont></td>\n\t<td width=\"80%\"><normalfont><b>$bbuserinfo[password]&nbsp;</b></normalfont></td>\n</tr>\n<tr>\n\t<td width=\"20%\"><normalfont><b>Email:</b></normalfont></td>\n\t<td width=\"80%\"><normalfont><b>$bbuserinfo[email]&nbsp;</b></normalfont></td>\n</tr>\n<tr>\n\t<td colspan=\"2\"><p><normalfont><b>Optional Information</b></normalfont></p></td>\n</tr>\n<tr>\n\t<td colspan=\"2\"><normalfont>To make changes to the profile information below, please visit this page and enter the username and password:<br>\n\t<a href=\"$bburl/member.php?s=$session[sessionhash]&action=editprofile\">$bburl/member.php?action=editprofile</a></normalfont></td>\n</tr>\n<tr>\n\t<td width=\"20%\"><normalfont><b>Homepage:</b></normalfont></td>\n\t<td width=\"80%\"><normalfont><b>$bbuserinfo[homepage]&nbsp;</b></normalfont></td>\n</tr>\n<tr>\n\t<td width=\"20%\"><normalfont><b>Signature:</b></normalfont></td>\n\t<td width=\"80%\"><normalfont><b>$bbuserinfo[signature]&nbsp;</b></normalfont></td>\n</tr>\n<tr>\n\t<td width=\"20%\"><normalfont><b>Privacy Mode:</b></normalfont><br>\n\t<smallfont>Selecting \'yes\' hides email, ICQ number, AIM ID in profile and also hides you in \'Logged In Users\' list.</smallfont></td>\n\t<td width=\"80%\"><normalfont><b>$bbuserinfo[showemail]&nbsp;</b></normalfont></td>\n</tr>\n<tr>\n\t<td width=\"20%\"><normalfont><b>ICQ Number:</b></normalfont></td>\n\t<td width=\"80%\"><normalfont><b>$bbuserinfo[icq]&nbsp;</b></normalfont></td>\n</tr>\n<tr bgcolor=\"#ffffff\">\n\t<td width=\"20%\"><normalfont><b>AOL Instant Messenger Handle:</b></normalfont></td>\n\t<td width=\"80%\"><normalfont><b>$bbuserinfo[aim]&nbsp;</b></normalfont></td>\n</tr>\n<tr>\n\t<td width=\"20%\"><normalfont><b>Yahoo Messenger Handle:</b></normalfont></td>\n\t<td width=\"80%\"><normalfont><b>$bbuserinfo[yahoo]&nbsp;</b></normalfont></td>\n</tr>\n<tr>\n\t<td colspan=\"2\"><p><normalfont><b>Please sign the form below and send it to us.</b></normalfont></p>\n\t<p><normalfont><b>I have reviewed the information my child has supplied and I have read the <a href=\"$privacyurl\">Privacy Policy</a> ($privacyurl) for the web site. I understand that the profile information may be changed by a using a password. I understand that I may ask for this registration profile be removed entirely.</normalfont></p></td>\n</tr>\n<tr>\n\t<td width=\"20%\"><normalfont><b>Parent/Guardian Full Name:</b></normalfont></td>\n\t<td width=\"80%\">&nbsp;</td>\n</tr>\n<tr>\n\t<td width=\"20%\"><normalfont><b>Signature:</b></normalfont></td>\n\t<td>&nbsp;</td>\n</tr>\n<tr>\n\t<td width=\"20%\"><normalfont><b>Relation to Child:</b></normalfont></td>\n\t<td width=\"80%\">&nbsp;</td>\n</tr>\n<tr>\n\t<td width=\"20%\"><p><normalfont><b>Telephone:</b></normalfont></td>\n\t<td width=\"80%\">&nbsp;</td>\n</tr>\n<tr>\n\t<td width=\"20%\"><normalfont><b>Email Address:</b></normalfont></td>\n\t<td width=\"80%\"><normalfont><b>$bbuserinfo[parentemail]&nbsp;</b></normalfont></td>\n</tr>\n<tr>\n\t<td width=\"20%\"><normalfont><b>Date:</b></normalfont></td>\n\t<td width=\"80%\">&nbsp;</td>\n</tr>\n<tr>\n\t<td colspan=\"2\"><p><normalfont><b>Please contact <a href=\"mailto:$webmasteremail\">$webmasteremail</a> with any questions</normalfont></b></p></td>\n</tr>\n</table>\n\n</body>\n</html>");
INSERT INTO template VALUES("38","-1","editpoll","{htmldoctype}\n<html>\n<head>\n<title>$bbtitle - Edit this poll</title>\n$headinclude\n</head>\n<body>\n$header\n\n<table cellpadding=\"2\" cellspacing=\"0\" border=\"0\" width=\"{contenttablewidth}\" {tableinvisibleextra} align=\"center\">\n<tr>\n\t<td nowrap>$navbar\n\t<p><smallfont><b>Forum Rules:</b><br>$forumrules</smallfont></p></td>\n</tr>\n</table>\n\n<br>\n\n<table cellpadding=\"{tableouterborderwidth}\" cellspacing=\"0\" border=\"0\" bgcolor=\"{tablebordercolor}\" {tableouterextra} width=\"{contenttablewidth}\" align=\"center\"><tr><td>\n<table cellpadding=\"4\" cellspacing=\"{tableinnerborderwidth}\" border=\"0\" {tableinnerextra} width=\"100%\">\n<form action=\"poll.php\" method=\"post\">\n<input type=\"hidden\" name=\"s\" value=\"$session[sessionhash]\">\n<tr>\n\t<td bgcolor=\"{tableheadbgcolor}\" colspan=\"2\"><normalfont color=\"{tableheadtextcolor}\"><b>Edit Poll</b></normalfont></td>\n</tr>\n<tr>\n\t<td bgcolor=\"{firstaltcolor}\" colspan=\"2\"><normalfont><input type=\"checkbox\" name=\"closepoll\" value=\"yes\" $pollinfo[closed]><b>Close?</b> To close this poll, check this box.<br>\n\t<smallfont>Note: Closing this poll makes it impossible to vote. It however does not stop people from replying to the thread</smallfont></td>\n</tr>\n\n$logincode\n\n<tr>\n\t<td bgcolor=\"{firstaltcolor}\" nowrap><normalfont><b>Question:</b></normalfont></td>\n\t<td bgcolor=\"{firstaltcolor}\"><normalfont><input type=\"text\" class=\"bginput\" name=\"pollquestion\" value=\"$pollinfo[question]\" size=\"40\" maxlength=\"85\"></normalfont></td>\n</tr>\n<tr>\n\t<td bgcolor=\"{secondaltcolor}\" valign=\"top\" nowrap><normalfont><b>Poll to edit:</b></normalfont>\n\t<p><br><smallfont>\n\tSee Forum Rules (above) for<br>\n\tmore information about what<br>\n\tcodes you are allowed to use<br>\n\tin your polls.</smallfont></p>\n\t</td>\n\t<td bgcolor=\"{secondaltcolor}\" valign=\"top\">\n\t\t<table border=\"0\" cellpadding=\"0\">\n\t\t$pollbits\n\t\t<tr>\n\t\t\t<td><b><normalfont>Additional Fields:</normalfont></b></td>\n\t\t</tr>\n\t\t<tr>\n\t\t\t<td><normalfont>Additional Option 1:</normalfont></td>\n\t\t\t<td><normalfont><input name=\"options[]\" type=\"text\" class=\"bginput\" size=\"45\"></normalfont></td>\n\t\t</tr>\n\t\t<tr>\n\t\t\t<td><normalfont>Additional Option 2:</normalfont></td>\n\t\t\t<td><normalfont><input name=\"options[]\" type=\"text\" class=\"bginput\" size=\"45\"></normalfont></td>\n\t\t</tr>\n\t\t</table>\n\t</td>\n</tr>\n<tr>\n\t<td bgcolor=\"{firstaltcolor}\" nowrap><normalfont><b>Time out:</b></normalfont></td>\n\t<td bgcolor=\"{firstaltcolor}\"><normalfont><input type=\"text\" class=\"bginput\" name=\"timeout\" value=\"$pollinfo[timeout]\" size=\"10\" maxlength=\"85\"> days after $pollinfo[postdate]</normalfont></td>\n</tr>\n</table>\n</td></tr></table>\n\n<br>\n\n<table cellpadding=\"2\" cellspacing=\"0\" border=\"0\" width=\"{contenttablewidth}\" {tableinvisibleextra} align=\"center\">\n<tr>\n\t<td align=\"center\"><normalfont>\n\t<input type=\"hidden\" name=\"action\" value=\"updatepoll\">\n\t<input type=\"hidden\" name=\"pollid\" value=\"$pollid\">\n\t<input type=\"submit\" class=\"bginput\" accesskey=\"s\" value=\"Submit Changes Now\">\n\t<input type=\"reset\" class=\"bginput\" value=\"Reset Fields\">\n\t</normalfont></td>\n</tr>\n</table>\n\n</form>\n\n$footer\n\n</body>\n</html>");
INSERT INTO template VALUES("39","-1","editpost","{htmldoctype}\n<html>\n<head>\n<title>$bbtitle - Edit this post</title>\n$headinclude\n<!--<script type=\"text/javascript\" language=\"javascript\" src=\"$bburl/spellcheck/spch.js\"></script>-->\n<script language=\"javascript\">\n<!--\nvar postmaxchars = $postmaxchars;\nfunction validate(theform) {\n\tif (theform.message.value==\"\") {\n\t\talert(\"Please complete the message field.\");\n\t\treturn false; }\n\tif (postmaxchars != 0) {\n\t\tif (theform.message.value.length > $postmaxchars) {\n\t\t\talert(\"Your message is too long.\\n\\nReduce your message to $postmaxchars characters.\\nIt is currently \"+theform.message.value.length+\" characters long.\");\n\t\t\treturn false; }\n\t\telse { return true; }\n\t} else { return true; }\n}\nfunction checklength(theform) {\n\tif (postmaxchars != 0) { message = \"\\nThe maximum permitted length is $postmaxchars characters.\"; }\n\telse { message = \"\"; }\n\talert(\"Your message is \"+theform.message.value.length+\" characters long.\"+message);\n}\n//-->\n</script>\n</head>\n<body>\n$header\n\n<table cellpadding=\"2\" cellspacing=\"0\" border=\"0\" width=\"{contenttablewidth}\" {tableinvisibleextra} align=\"center\">\n<tr>\n\t<td width=\"100%\">$navbar</td>\n</tr>\n</table>\n\n<br>\n\n<form action=\"editpost.php\" method=\"post\">\n<input type=\"hidden\" name=\"s\" value=\"$session[sessionhash]\">\n<input type=\"hidden\" name=\"postid\" value=\"$postid\">\n<input type=\"hidden\" name=\"action\" value=\"deletepost\">\n<table cellpadding=\"{tableouterborderwidth}\" cellspacing=\"0\" border=\"0\" bgcolor=\"{tablebordercolor}\" {tableouterextra} width=\"{contenttablewidth}\" align=\"center\"><tr><td>\n<table cellpadding=\"4\" cellspacing=\"{tableinnerborderwidth}\" border=\"0\" {tableinnerextra} width=\"100%\">\n<tr>\n\t<td bgcolor=\"{tableheadbgcolor}\" colspan=\"2\"><normalfont color=\"{tableheadtextcolor}\"><b>Delete Post</b></normalfont></td>\n</tr>\n<tr>\n\t<td bgcolor=\"{firstaltcolor}\" colspan=\"2\">\n\t<table border=\"0\" align=\"center\">\n\t<tr valign=\"top\">\n\t\t<td nowrap><normalfont><input type=\"checkbox\" name=\"deletepost\" value=\"yes\"> <b>Delete?</b></normalfont></td>\n\t\t<td width=\"100%\"><normalfont>To delete this post, check the box to the left and then click the button to the right.<br><smallfont>Note: deleting this post will result in the deletion of the entire thread if this post is the first post in the thread.</smallfont></normalfont></td>\n\t\t<td nowrap><normalfont><input type=\"submit\" class=\"bginput\" name=\"SUBMIT\" value=\"Delete Now\"></normalfont></td>\n\t</tr>\n\t</table>\n\t</td>\n</tr>\n</table>\n</td></tr></table>\n</form>\n\n<form $enctype name=\"vbform\" action=\"editpost.php\" method=\"post\" onSubmit=\"return validate(this)\">\n<input type=\"hidden\" name=\"s\" value=\"$session[sessionhash]\">\n<input type=\"hidden\" name=\"action\" value=\"updatepost\">\n<input type=\"hidden\" name=\"postid\" value=\"$postid\">\n\n<table cellpadding=\"{tableouterborderwidth}\" cellspacing=\"0\" border=\"0\" bgcolor=\"{tablebordercolor}\" {tableouterextra} width=\"{contenttablewidth}\" align=\"center\"><tr><td>\n<table cellpadding=\"4\" cellspacing=\"{tableinnerborderwidth}\" border=\"0\" {tableinnerextra} width=\"100%\">\n<tr>\n\t<td bgcolor=\"{tableheadbgcolor}\" colspan=\"2\"><normalfont color=\"{tableheadtextcolor}\"><b>Edit Post</b></normalfont></td>\n</tr>\n$logincode\n<tr>\n\t<td bgcolor=\"{secondaltcolor}\" nowrap><normalfont><b>Subject:</b></normalfont></td>\n\t<td bgcolor=\"{secondaltcolor}\"><normalfont><input type=\"text\" class=\"bginput\" name=\"title\" value=\"$postinfo[title]\" size=\"40\" maxlength=\"85\" tabindex=\"1\"></normalfont></td>\n</tr>\n$posticons\n$vbcode_buttons\n<tr>\n\t<td bgcolor=\"{secondaltcolor}\" valign=\"top\" nowrap><normalfont><b>Message to edit:</b></normalfont>\n\t<p><br><smallfont>\n\tSee Forum Rules (below) for<br>\n\tmore information about what<br>\n\tcodes you are allowed to use<br>\n\tin your posts.</smallfont></p>\n\t$vbcode_smilies</td>\n\t<td bgcolor=\"{secondaltcolor}\"><smallfont>Originally posted by $postinfo[username] on $postinfo[postdate] $postinfo[posttime]<br></smallfont>\n\t<table cellpadding=\"0\" cellspacing=\"0\" border=\"0\">\n\t<tr valign=\"top\">\n\t\t<td><textarea name=\"message\" rows=\"20\" cols=\"$textareacols\" wrap=\"virtual\" tabindex=\"2\">$postinfo[message]</textarea><br>\n\t\t<smallfont><a href=\"javascript:checklength(document.vbform);\">[check message length]</a></smallfont></td>\n\t</tr>\n\t</table>\n\t</td>\n</tr>\n<tr>\n\t<td bgcolor=\"{firstaltcolor}\" valign=\"top\"><normalfont><b>Options:</b></normalfont></td>\n\t<td bgcolor=\"{firstaltcolor}\" valign=\"top\"><smallfont>\n\t<input type=\"checkbox\" name=\"parseurl\" value=\"yes\" $parseurlchecked> <b>Automatically parse URLs:</b> automatically adds [url] and [/url] around internet addresses.\n\t<br><input type=\"checkbox\" name=\"email\" $emailchecked value=\"yes\"> <b>Email Notification:</b> emails sent to you whenever someone replies. Only registered users are eligible.\n\t$disablesmiliesoption\n\t<br><input type=\"checkbox\" name=\"signature\" $signaturechecked value=\"yes\"> <b>Show Signature:</b> include your profile signature.  Only registered users may have signatures.\n\t</smallfont></td>\n</tr>\n$editattachment\n</table>\n</td></tr></table>\n\n<br>\n\n<table cellpadding=\"2\" cellspacing=\"0\" border=\"0\" width=\"{contenttablewidth}\" {tableinvisibleextra} align=\"center\">\n<tr>\n\t<td align=\"center\"><normalfont>\n\t<input type=\"submit\" class=\"bginput\" name=\"submit\" accesskey=\"s\" value=\"Save Changes\" tabindex=\"3\">\n\t<!--<input type=\"button\" class=\"bginput\" value=\"Spell Check\" onclick=\"var f=document.forms[\'vbform\']; doSpell( \'$spellchecklang\', f.message, \'$bburl/spellcheck/sproxy.php\', true);\">-->\n\t<input type=\"reset\" class=\"bginput\" value=\"Reset Message\" tabindex=\"4\">\n\t</normalfont></td>\n</tr>\n</table>\n\n</form>\n\n<table cellpadding=\"2\" cellspacing=\"0\" border=\"0\" width=\"{contenttablewidth}\" {tableinvisibleextra} align=\"center\">\n<tr>\n\t<td><smallfont><b>Forum Rules:</b><br>$forumrules</smallfont></td>\n</tr>\n</table>\n\n$footer\n\n</body>\n</html>");
INSERT INTO template VALUES("40","-1","editpost_attachment","<tr>\n\t<td bgcolor=\"{secondaltcolor}\"><normalfont><b>Attachment:</b></normalfont><br>\n\t<smallfont>Valid file extensions: $attachextensions<br>\n\tMaximum size: $maxattachsize bytes</smallfont></td>\n\t<td bgcolor=\"{secondaltcolor}\"><smallfont>\n\t<input type=\"radio\" name=\"attachmentaction\" value=\"keep\" checked> Keep current attachment\n\t( <a href=\"attachment.php?s=$session[sessionhash]&postid=$postinfo[postid]\" target=\"_blank\">$postinfo[filename]</a> )<br>\n\t<input type=\"radio\" name=\"attachmentaction\" value=\"delete\"> Delete current attachment<br>\n\t<input type=\"radio\" name=\"attachmentaction\" value=\"new\"> Upload new attachment:\n\t<input type=\"hidden\" name=\"MAX_FILE_SIZE\" value=\"$maxattachsize_temp\">\n\t<input type=\"file\" class=\"bginput\" name=\"attachment\">\n\t</smallfont></td>\n</tr>\n");
