INSERT INTO template VALUES("432","-1","subscribe","{htmldoctype}\n<html>\n<head>\n<title>Subscribed threads - $bbtitle</title>\n$headinclude\n<script language=\"Javascript\">\nfunction who(threadid) { window.open(\"misc.php?s=$session[sessionhash]&action=whoposted&threadid=\"+threadid, \"whoposted\", \"toolbar=no,scrollbars=yes,resizable=yes,width=230,height=300\"); }\n</script>\n</head>\n<body>\n$header\n\n<!-- breadcrumb -->\n<table cellpadding=\"2\" cellspacing=\"0\" border=\"0\" width=\"{contenttablewidth}\" {tableinvisibleextra} align=\"center\">\n<tr>\n\t<td width=\"100%\"><img src=\"{imagesfolder}/vb_bullet.gif\" align=\"middle\" alt=\"vBulletin\">\n\t<normalfont><b><a href=\"index.php?s=$session[sessionhash]\">$bbtitle</a> &gt;\n\tUser Control Panel For $bbuserinfo[username]</b></normalfont></td>\n</tr>\n</table>\n<!-- /breadcrumb -->\n\n<br>\n$cpnav\n<br>\n\n<table cellpadding=\"2\" cellspacing=\"0\" border=\"0\" width=\"{contenttablewidth}\" {tableinvisibleextra} align=\"center\">\n<tr>\n\t<td align=\"right\"><smallfont><a href=\"member2.php?s=$session[sessionhash]&action=removesubscription&type=allthread\"><b>Unsubscribe From All Threads</b></a></smallfont></td>\n</tr>\n</table>\n\n<br>\n\n<table cellpadding=\"0\" cellspacing=\"0\" border=\"0\"  width=\"{contenttablewidth}\" align=\"center\"><tr><td>\n$threadslist\n</td></tr></table>\n\n<!-- forum jump and daysprune -->\n<table cellpadding=\"2\" cellspacing=\"0\" border=\"0\" width=\"{contenttablewidth}\" {tableinvisibleextra} align=\"center\">\n<tr valign=\"top\">\n\t<td nowrap><normalfont>$forumjump</normalfont></td>\n\t<td align=\"right\"><smallfont>$timezone</smallfont></td>\n</tr>\n</table>\n<!-- /forum jump and daysprune -->\n\n<br>\n\n<!-- icon key -->\n<table cellpadding=\"2\" cellspacing=\"0\" border=\"0\" width=\"{contenttablewidth}\" {tableinvisibleextra} align=\"center\">\n<tr><td rowspan=\"2\" width=\"50%\">&nbsp;</td>\n\t<td nowrap><smallfont><img src=\"{imagesfolder}/newfolder.gif\" border=\"0\" alt=\"New Posts\"> <b>New posts</b> &nbsp;&nbsp;</smallfont></td>\n\t<td nowrap><smallfont>( <img src=\"{imagesfolder}/newhotfolder.gif\" border=0 alt=\"Hot Thread\"> <b>More than $hotnumberposts replies or $hotnumberviews views</b> )&nbsp;&nbsp;</smallfont></td>\n\t<td nowrap><smallfont><img src=\"{imagesfolder}/lockfolder.gif\" border=\"0\" alt=\"Closed Thread\"> <b>Closed thread</b></smallfont></td>\n<td rowspan=\"2\" width=\"50%\">&nbsp;</td></tr>\n<tr>\n\t<td nowrap><smallfont><img src=\"{imagesfolder}/folder.gif\" border=\"0\" alt=\"No New Posts\"> <b>No new posts</b>&nbsp;&nbsp;</smallfont></td>\n\t<td nowrap colspan=\"2\"><smallfont>( <img src=\"{imagesfolder}/hotfolder.gif\" border=0 alt=\"Hot Thread\"> <b>More than $hotnumberposts replies or $hotnumberviews views</b> )</smallfont></td>\n</tr>\n</table>\n<!-- /icon key -->\n\n$footer\n</body>\n</html>\n");
