INSERT INTO template VALUES("2069","1","vbcode_buttons","<script language=\"Javascript\" src=\"vbcode.js\"></script>\r\n<script language=\"Javascript\" src=\"vbcode_language.js\"></script>\r\n<style>\r\n#codebuttons input { font-family: verdana, arial, helvetica; font-size: 11px; }\r\n#codebuttons select { font-family: verdana, arial, helvetica; font-size: 11px; }\r\n.smilieTable { border-width: 2px; border-style: outset; }\r\n</style>\r\n\r\n<tr id=\"codebuttons\">\r\n        <td bgcolor=\"{secondaltcolor}\" valign=\"top\"><normalfont><b>vB Code</b></normalfont>\r\n        <smallfont>\r\n        [<a href=\"misc.php?action=bbcode#buttons\" target=\"_blank\">Hilfe</a>]<br>\r\n        <input type=\"radio\" name=\"mode\" value=\"0\" title=\"Normaler Modus: (alt+n)\" accesskey=\"n\"\r\n        onclick=\"setmode(this.value)\" onmouseover=\"stat(\'norm\')\" $modechecked[0]> Normaler Modus\r\n        <br>\r\n        <input type=\"radio\" name=\"mode\" value=\"1\" title=\"Erweiterter Modus: (alt+e)\" accesskey=\"e\"\r\n        onclick=\"setmode(this.value)\" onmouseover=\"stat(\'enha\')\" $modechecked[1]> Erweiterter Modus\r\n        </smallfont></td>\r\n\r\n        <td bgcolor=\"{secondaltcolor}\">\r\n\r\n        <table cellpadding=\"0\" cellspacing=\"0\" border=\"0\">\r\n        <tr>\r\n                <td nowrap><smallfont><p>\r\n<select id=\"sizeselect\" \r\nonchange=\"fontformat(this.form,this.options[this.selectedIndex].value,\'SIZE\')\" onmouseover=\"stat(\'size\')\">\r\n<option value=\"0\">SIZE\r\n$vbcode_sizebits\r\n</select\r\n\r\n><select id=\"alignselect\" \r\nonchange=\"fontformat(this.form,this.options[this.selectedIndex].value,\'ALIGN\')\" onmouseover=\"stat(\'align\')\" name=\"1\">\r\n<option value=\"0\">AUSRICHTUNG $vbcode_alignbits\r\n</select\r\n\r\n><select id=\"colorselect\" \r\nonchange=\"fontformat(this.form,this.options[this.selectedIndex].value,\'COLOR\')\" onmouseover=\"stat(\'color\')\">\r\n<option value=\"0\">COLOR\r\n$vbcode_colorbits\r\n</selec