INSERT INTO template VALUES("1822","1","modifyavatar","{htmldoctype}\r\n<html>\r\n<head>\r\n<title>$bbtitle - Einstellungen bearbeiten</title>\r\n$headinclude\r\n</head>\r\n<body>\r\n$header\r\n\r\n<!-- breadcrumb -->\r\n<table cellpadding=\"2\" cellspacing=\"0\" border=\"0\" width=\"{contenttablewidth}\" {tableinvisibleextra} align=\"center\">\r\n<tr>\r\n        <td width=\"100%\"><img src=\"{imagesfolder}/vb_bullet.gif\" align=\"middle\" alt=\"vBulletin\">\r\n        <normalfont><b><a href=\"index.php?s=$session[sessionhash]\">$bbtitle</a> &gt;\r\n        <a href=\"usercp.php?s=$session[sessionhash]\">Kontrollzentrum von $bbuserinfo[username]</a></b></normalfont></td>\r\n</tr>\r\n</table>\r\n<!-- /breadcrumb -->\r\n\r\n<br>\r\n$cpnav\r\n<br>\r\n\r\n<form $enctype action=\"member.php\" method=\"post\">\r\n<input type=\"hidden\" name=\"s\" value=\"$session[sessionhash]\">\r\n\r\n<table cellpadding=\"{tableouterborderwidth}\" cellspacing=\"0\" border=\"0\" bgcolor=\"{tablebordercolor}\" {tableouterextra} width=\"{contenttablewidth}\" align=\"center\"><tr><td>\r\n<table cellpadding=\"4\" cellspacing=\"{tableinnerborderwidth}\" border=\"0\" {tableinnerextra} width=\"100%\">\r\n<tr>\r\n        <td bgcolor=\"{categorybackcolor}\" colspan=\"2\"><normalfont color=\"{categoryfontcolor}\"><b>Benutzerbild bearbeiten - $bbuserinfo[username]</b></normalfont></td>\r\n</tr>\r\n<tr>\r\n        <td bgcolor=\"{tableheadbgcolor}\" colspan=\"2\"><normalfont color=\"{tableheadtextcolor}\"><b>Benutzerbilder</b></normalfont></td>\r\n</tr>\r\n<tr>\r\n        <td bgcolor=\"{secondaltcolor}\"><normalfont><b>Benutzerbild verwenden </b></normalfont><br><smallfont>Das Benutzerbild ist eine kleine Grafik, die in jedem Beitrag unter Deinem Benutzernamen angezeigt wird.</smallfont></td>\r\n        <td bgcolor=\"{secondaltcolor}\"><normalfont><input type=\"radio\" name=\"avatarid\" value=\"-1\" $nouseavatarchecked> nein</normalfont><br>\r\n        <smallfont>(Wenn Du diese Option wählst, wird Dein eigenes Benutzerbild gelöscht)</smallfont></td>\r\n</tr>\r\n<tr>\r\n        <td bgcolor=\"{firstaltcolor}\" align=\"center\" colspan=\"2\"><normalfont><b>Wähle Dein Benutzerbild aus dieser Liste:</b></normalfont></td>\r\n</tr>\r\n<tr>\r\n        <td bgcolor=\"{secondaltcolor}\" colspan=\"2\" align=\"center\">\r\n        <table bgcolor=\"{tablebordercolor}\" border=\"0\" cellspacing=\"1\" cellpadding=\"10\">\r\n        $avatarlist\r\n        </table>\r\n        <normalfont>Zeige Benutzerbilder $limitlower bis $limitupper von $totalavatars<br>$pagenav</normalfont>\r\n        </td>\r\n</tr>\r\n$customavatar\r\n</table>\r\n</td></tr></table>\r\n\r\n<br>\r\n\r\n<table cellpadding=\"2\" cellspacing=\"0\" border=\"0\" width=\"{contenttablewidth}\" {tableinvisibleextra} align=\"center\">\r\n<tr>\r\n        <td align=\"center\"><normalfont>\r\n<input type=\"hidden\" name=\"action\" value=\"updateavatar\">\r\n<input type=\"submit\" class=\"bginput\" name=\"Submit\" value=\"Änderungen speichern\" accesskey=\"s\">\r\n<input type=\"reset\" class=\"bginput\" name=\"reset\" value=\"Felder löschen\">\r\n        </normalfont></td>\r\n</tr>\r\n</table>\r\n\r\n</form>\r\n\r\n$footer\r\n\r\n</body>\r\n</html>");
