INSERT INTO template VALUES("2009","1","searchintro","{htmldoctype}\r\n<html>\r\n<head><title>$bbtitle Suchen...</title>\r\n$headinclude\r\n</head>\r\n<body>\r\n$header\r\n\r\n<!-- breadcrumb -->\r\n<table cellpadding=\"2\" cellspacing=\"0\" border=\"0\" width=\"{contenttablewidth}\" {tableinvisibleextra} align=\"center\">\r\n<tr>\r\n        <td width=\"100%\">$navbar</td>\r\n</tr>\r\n</table>\r\n<!-- /breadcrumb -->\r\n\r\n<br>\r\n\r\n<form action=\"search.php\" method=\"post\">\r\n<input type=\"hidden\" name=\"s\" value=\"$session[sessionhash]\">\r\n\r\n<table cellpadding=\"{tableouterborderwidth}\" cellspacing=\"0\" border=\"0\" bgcolor=\"{tablebordercolor}\" {tableouterextra} width=\"{contenttablewidth}\" align=\"center\"><tr><td>\r\n<table cellpadding=\"4\" cellspacing=\"{tableinnerborderwidth}\" border=\"0\" {tableinnerextra} width=\"100%\">\r\n<tr>\r\n        <td bgcolor=\"{categorybackcolor}\" colspan=\"2\"><normalfont color=\"{categoryfontcolor}\"><b>\r\n        $bbtitle Suchen...\r\n        </b></normalfont></td>\r\n</tr>\r\n<tr>\r\n        <td bgcolor=\"{tableheadbgcolor}\"><smallfont color=\"{tableheadtextcolor}\"><b>Suche nach Schlsselwort</b></smallfont></td>\r\n        <td bgcolor=\"{tableheadbgcolor}\"><smallfont color=\"{tableheadtextcolor}\"><b>Benutzernamen suchen</b></smallfont></td>\r\n</tr>\r\n<tr valign=\"top\">\r\n        <td bgcolor=\"{firstaltcolor}\"><smallfont>\r\n        <br><input type=\"text\" class=\"bginput\" name=\"query\" size=\"35\">\r\n        <br><br>\r\n        <b>Einfache Suche:</b>\r\n        Trenne Deine Suchbegriffe mit Leerzeichen voneinander.<br>\r\n        <br>\r\n        <b>Erweiterte Suche:</b>\r\n        Benutze AND, OR und NOT in Verbindung mit Deinen Suchbegriffen, um detaillierter zu suchen.<br>\r\n        Du kannst Sternchen (*) als Platzhalter in den Suchbegriff einfgen. (eine Suche nach <i>*bullet*</i> findet z.B. auch <i>vBulletin</i> etc.)\r\n        </smallfont></td>\r\n        <!-- *** -->\r\n        <td bgc