INSERT INTO template VALUES("1608","1","editpoll","{htmldoctype}\r\n<html>\r\n<head>\r\n<title>$bbtitle - Umfrage bearbeiten</title>\r\n$headinclude\r\n</head>\r\n<body>\r\n$header\r\n\r\n<table cellpadding=\"2\" cellspacing=\"0\" border=\"0\" width=\"{contenttablewidth}\" {tableinvisibleextra} align=\"center\">\r\n<tr>\r\n        <td nowrap>$navbar\r\n                <p>$forumrules</p></td>\r\n</tr>\r\n</table>\r\n\r\n<br>\r\n\r\n<table cellpadding=\"{tableouterborderwidth}\" cellspacing=\"0\" border=\"0\" bgcolor=\"{tablebordercolor}\" {tableouterextra} width=\"{contenttablewidth}\" align=\"center\"><tr><td>\r\n<table cellpadding=\"4\" cellspacing=\"{tableinnerborderwidth}\" border=\"0\" {tableinnerextra} width=\"100%\">\r\n<form action=\"poll.php\" method=\"post\">\r\n<input type=\"hidden\" name=\"s\" value=\"$session[sessionhash]\">\r\n<tr>\r\n        <td bgcolor=\"{tableheadbgcolor}\" colspan=\"2\"><normalfont color=\"{tableheadtextcolor}\"><b>Umfrage bearbeiten</b></normalfont></td>\r\n</tr>\r\n<tr>\r\n        <td bgcolor=\"{firstaltcolor}\" colspan=\"2\"><normalfont><input type=\"checkbox\" name=\"closepoll\" value=\"yes\" $pollinfo[closed]><b>Schliessen?</b> Um diese Umfrage zu schliessen, markiere das Kästchen.<br>\r\n        <smallfont>Anmerkung: Wenn Du diese Umfrage schliesst, kann nicht mehr abgestimmt werden. Es können jedoch weiterhin Antworten zu diesem Thema verfasst werden.</smallfont></td>\r\n</tr>\r\n\r\n$logincode\r\n\r\n<tr>\r\n        <td bgcolor=\"{firstaltcolor}\" nowrap><normalfont><b>Frage:</b></normalfont></td>\r\n        <td bgcolor=\"{firstaltcolor}\"><normalfont><input type=\"text\" class=\"bginput\" name=\"pollquestion\" value=\"$pollinfo[question]\" size=\"40\" maxlength=\"85\"></normalfont></td>\r\n</tr>\r\n<tr>\r\n        <td bgcolor=\"{secondaltcolor}\" valign=\"top\" nowrap><normalfont><b>Zu bearbeitende Umfrage:</b></normalfont>\r\n        <p><br><smallfont>\r\n        Siehe Forum Regeln (oben),<br>\r\n        welche Texformatie