INSERT INTO template VALUES("1609","1","editpost","{htmldoctype}\r\n<html>\r\n<head>\r\n<title>$bbtitle - Beitrag bearbeiten</title>\r\n$headinclude\r\n<!--<script type=\"text/javascript\" language=\"javascript\" src=\"$bburl/spellcheck/spch.js\"></script>-->\r\n<script language=\"javascript\">\r\n<!--\r\nvar postmaxchars = $postmaxchars;\r\nfunction validate(theform) {\r\n        if (theform.message.value==\"\") {\r\n                alert(\"Bitte fülle das Beitragsfeld aus.\");\r\n                return false; }\r\n        if (postmaxchars != 0) {\r\n                if (theform.message.value.length > $postmaxchars) {\r\n                        alert(\"Dein Beitrag ist zu lang.\\n\\nVerkürze Deinen Beitrag auf $postmaxchars Zeichen.\\nDein Beitrag ist \"+theform.message.value.length+\" Zeichen lang.\");\r\n                        return false; }\r\n                else { return true; }\r\n        } else { return true; }\r\n}\r\nfunction checklength(theform) {\r\n        if (postmaxchars != 0) { message = \"\\nDein Beitrag darf maximal $postmaxchars Zeichen lang sein.\"; }\r\n        else { message = \"\"; }\r\n        alert(\"Dein Beitrag ist \"+theform.message.value.length+\" Zeichen lang.\"+message);\r\n}\r\n//-->\r\n</script>\r\n</head>\r\n<body>\r\n$header\r\n\r\n<table cellpadding=\"2\" cellspacing=\"0\" border=\"0\" width=\"{contenttablewidth}\" {tableinvisibleextra} align=\"center\">\r\n<tr>\r\n        <td width=\"100%\">$navbar</td>\r\n</tr>\r\n</table>\r\n\r\n<br>\r\n\r\n<form action=\"editpost.php\" method=\"post\">\r\n<input type=\"hidden\" name=\"s\" value=\"$session[sessionhash]\">\r\n<input type=\"hidden\" name=\"postid\" value=\"$postid\">\r\n<input type=\"hidden\" name=\"action\" value=\"deletepost\">\r\n<table cellpadding=\"{tableouterborderwidth}\" cellspacing=\"0\" border=\"0\" bgcolor=\"{tablebordercolor}\" {tableouterextra} width=\"{contenttablewidth}\" align=\"center\"><tr><td>\r\n<table cellpadding=\"4\" cellspacing=\"{tableinnerborderwidth}\"