INSERT INTO template VALUES("1","-1","activateform","{htmldoctype}\n<html>\n<head><title>$bbtitle - Activate registration</title>\n$headinclude\n</head>\n<body>\n$header\n\n<br>\n\n<form action=\"register.php\" method=\"post\">\n<input type=\"hidden\" name=\"s\" value=\"$session[sessionhash]\">\n<input type=\"hidden\" name=\"action\" value=\"activate\">\n\n<table cellpadding=\"{tableouterborderwidth}\" cellspacing=\"0\" border=\"0\" bgcolor=\"{tablebordercolor}\" {tableouterextra} width=\"{contenttablewidth}\" align=\"center\"><tr><td>\n<table cellpadding=\"4\" cellspacing=\"{tableinnerborderwidth}\" border=\"0\" {tableinnerextra} width=\"100%\">\n<tr>\n\t<td bgcolor=\"{tableheadbgcolor}\" colspan=\"2\"><normalfont color=\"{tableheadtextcolor}\"><b>Activate Your Account</b></normalfont></td>\n</tr>\n<tr>\n\t<td bgcolor=\"{firstaltcolor}\"><normalfont><b>User Name:</b></normalfont></td>\n\t<td bgcolor=\"{firstaltcolor}\"><input type=\"text\" class=\"bginput\" size=\"25\" name=\"username\" value=\"$bbuserinfo[username]\"></td>\n</tr>\n<tr>\n\t<td bgcolor=\"{secondaltcolor}\"><normalfont><b>Activation code:</b></normalfont></td>\n\t<td bgcolor=\"{secondaltcolor}\"><input type=\"text\" class=\"bginput\" size=\"25\" name=\"activateid\"></td>\n</tr>\n</table>\n</td></tr></table>\n\n<table cellpadding=\"2\" cellspacing=\"0\" border=\"0\" width=\"{contenttablewidth}\" {tableinvisibleextra} align=\"center\">\n<tr>\n\t<td align=\"center\"><normalfont><input type=\"submit\" class=\"bginput\" value=\"Submit Now\"></normalfont></td>\n</tr>\n</table>\n\n</form>\n\n$footer\n\n</body>\n</html>\n");
INSERT INTO template VALUES("2","-1","activate_requestemail","{htmldoctype}\n<html>\n<head>\n<title>$bbtitle - Retrieve activation code</title>\n$headinclude\n</head>\n<body>\n$header\n\n<br>\n\n<form action=\"register.php\" method=\"post\">\n<input type=\"hidden\" name=\"s\" value=\"$session[sessionhash]\">\n\n<table cellpadding=\"{tableouterborderwidth}\" cellspacing=\"0\" border=\"0\" bgcolor=\"{tablebordercolor}\" {tableouterextra} width=\"{contenttablewidth}\" align=\"center\"><tr><td>\n<table cellpadding=\"4\" cellspacing=\"{tableinnerborderwidth}\" border=\"0\" {tableinnerextra} width=\"100%\">\n<tr>\n\t<td bgcolor=\"{tableheadbgcolor}\" colspan=\"2\"><normalfont color=\"{tableheadtextcolor}\" class=\"thtcolor\"><b>Activation Code Request Form</b></normalfont></td>\n</tr>\n<tr>\n\t<td bgcolor=\"{firstaltcolor}\" valign=\"top\"><normalfont>To request for your activation code to be sent to you by email, please enter your registered email address in the box below then submit the form.</normalfont></td>\n</tr>\n<tr>\n\t<td bgcolor=\"{secondaltcolor}\" align=\"center\" nowrap><normalfont><b>Your Email Address: &nbsp;&nbsp;&nbsp;\n\t<input type=\"text\" class=\"bginput\" name=\"email\" size=\"35\" maxlength=\"90\"></b></normalfont></td>\n</tr>\n</table>\n</td></tr></table>\n\n<br>\n\n<table cellpadding=\"2\" cellspacing=\"0\" border=\"0\" width=\"{contenttablewidth}\" {tableinvisibleextra} align=\"center\">\n<tr>\n\t<td align=\"center\"><normalfont>\n\t<input type=\"hidden\" name=\"action\" value=\"emailcode\">\n\t<input type=\"hidden\" name=\"url\" value=\"$url\">\n\t<input type=\"submit\" class=\"bginput\" name=\"Submit\" VALUE=\"Request Activation Code Now\">\n\t</normalfont></td>\n</tr>\n</table>\n\n</form>\n\n$footer\n\n</body>\n</html>");
