INSERT INTO template VALUES("257","-1","modifyoptions","{htmldoctype}\n<html>\n<head>\n<title>$bbtitle - Change options</title>\n$headinclude\n</head>\n<body>\n$header\n\n<!-- breadcrumb -->\n<table cellpadding=\"2\" cellspacing=\"0\" border=\"0\" width=\"{contenttablewidth}\" {tableinvisibleextra} align=\"center\">\n<tr>\n\t<td width=\"100%\"><img src=\"{imagesfolder}/vb_bullet.gif\" align=\"middle\" alt=\"vBulletin\">\n\t<normalfont><b><a href=\"index.php?s=$session[sessionhash]\">$bbtitle</a> &gt;\n\t<a href=\"usercp.php?s=$session[sessionhash]\">User Control Panel For $bbuserinfo[username]</a> &gt;\n\tEdit Options</b></normalfont></td>\n</tr>\n</table>\n<!-- /breadcrumb -->\n\n<br>\n$cpnav\n<br>\n\n<form action=\"member.php\" method=\"post\"><input type=\"hidden\" name=\"s\" value=\"$session[dbsessionhash]\">\n\n<table cellpadding=\"{tableouterborderwidth}\" cellspacing=\"0\" border=\"0\" bgcolor=\"{tablebordercolor}\" {tableouterextra} width=\"{contenttablewidth}\" align=\"center\"><tr><td>\n<table cellpadding=\"4\" cellspacing=\"{tableinnerborderwidth}\" border=\"0\" {tableinnerextra} width=\"100%\">\n<tr>\n\t<td bgcolor=\"{categorybackcolor}\" colspan=\"2\"><normalfont color=\"{categoryfontcolor}\"><b>Edit Options - $bbuserinfo[username]</b></normalfont></td>\n</tr>\n<!-- *** -->\n<tr>\n\t<td bgcolor=\"{tableheadbgcolor}\" colspan=\"2\"><normalfont color=\"{tableheadtextcolor}\"><b>Login, Cookies &amp; Privacy</b></normalfont></td>\n</tr>\n<!-- *** -->\n<tr>\n\t<td bgcolor=\"{secondaltcolor}\"><normalfont><b>Invisible Mode?</b><br>\n\t<smallfont>Selecting yes allows you to surf the forums without appearing in the \'Logged In Users\' list.</smallfont></normalfont></td>\n\t<td bgcolor=\"{secondaltcolor}\"><normalfont>\n\t\t<input type=\"radio\" name=\"invisible\" value=\"yes\" $invisiblechecked> yes\n\t\t<input type=\"radio\" name=\"invisible\" value=\"no\" $invisiblenotchecked> no\n\t</normalfont></td>\n</tr>\n<tr>\n\t<td bgcolor=\"{firstaltcolor}\"><normalfont><b>Automatically login when you return to the site? (uses cookies)</b></normalfont></td>\n\t<td bgcolor=\"{firstaltcolor}\"><normalfont>\n\t\t<input type=\"radio\" name=\"cookieuser\" $cookieuserchecked value=\"yes\"> yes\n\t\t<input type=\"radio\" name=\"cookieuser\" $cookieusernotchecked value=\"no\"> no\n\t</normalfont></td>\n</tr>\n<tr>\n\t<td bgcolor=\"{secondaltcolor}\"><normalfont><b>Browse board with cookies?</b></normalfont><br>\n\t<smallfont>Clicking yes will use cookies to keep your id for this session. Clicking no will send it through the links. (Selecting no may cause problems when sharing links with people behind the same proxy as you)</smallfont></td>\n\t<td bgcolor=\"{secondaltcolor}\"><normalfont>\n\t\t<input type=\"radio\" name=\"nosessionhash\" $nosessionhashchecked value=\"yes\"> yes\n\t\t<input type=\"radio\" name=\"nosessionhash\" $nosessionhashnotchecked value=\"no\"> no\n\t</normalfont></td>\n</tr>\n<!-- *** -->\n<tr>\n\t<td bgcolor=\"{tableheadbgcolor}\" colspan=\"2\"><normalfont color=\"{tableheadtextcolor}\"><b>Messaging &amp; Notification</b></normalfont></td>\n</tr>\n<!-- *** -->\n<tr>\n\t<td bgcolor=\"{secondaltcolor}\"><normalfont><b>Allow Bulletin Board Administrators and Moderators To Send You Email Notices?</b></normalfont></td>\n\t<td bgcolor=\"{secondaltcolor}\"><normalfont>\n\t\t<input type=\"radio\" name=\"allowmail\" value=\"yes\" $allowmailchecked> yes\n\t\t<input type=\"radio\" name=\"allowmail\" value=\"no\" $allowmailnotchecked> no\n\t</normalfont></td>\n</tr>\n<tr>\n\t<td bgcolor=\"{firstaltcolor}\"><normalfont><b>Hide Email Address?</b><br>\n\t<smallfont>Clicking yes hides your email address in your public profile, so that it is not accessible by other users.</smallfont></normalfont></td>\n\t<td bgcolor=\"{firstaltcolor}\"><normalfont>\n\t\t<input type=\"radio\" name=\"showemail\" value=\"no\" $showemailnotchecked> yes\n\t\t<input type=\"radio\" name=\"showemail\" value=\"yes\" $showemailchecked> no\n\t</normalfont></td>\n</tr>\n<tr>\n\t<td bgcolor=\"{secondaltcolor}\"><normalfont><b>Use \'Email Notification\' by default?</b></normalfont><br>\n\t<smallfont>Using this option emails you whenever someone replies to a thread that you have participated in.</smallfont></td>\n\t<td bgcolor=\"{secondaltcolor}\"><normalfont>\n\t\t<input type=\"radio\" name=\"emailnotification\" value=\"yes\" $emailnotificationchecked> yes\n\t\t<input type=\"radio\" name=\"emailnotification\" value=\"no\" $emailnotificationnotchecked> no\n\t</normalfont></td>\n</tr>\n<tr>\n\t<td bgcolor=\"{firstaltcolor}\"><normalfont><b>Enable Private Messaging?</b><br>\n\t<smallfont>Allows people to send Private Messages to you</smallfont></normalfont></td>\n\t<td bgcolor=\"{firstaltcolor}\"><normalfont>\n\t\t<input type=\"radio\" name=\"receivepm\" $receivepmchecked value=\"yes\"> yes\n\t\t<input type=\"radio\" name=\"receivepm\" $receivepmnotchecked value=\"no\"> no\n\t</normalfont></td>\n</tr>\n<tr>\n\t<td bgcolor=\"{secondaltcolor}\"><normalfont><b>Send you an email when you receive a Private Message?</b></normalfont></td>\n\t<td bgcolor=\"{secondaltcolor}\"><normalfont>\n\t\t<input type=\"radio\" name=\"emailonpm\" $emailonpmchecked value=\"yes\"> yes\n\t\t<input type=\"radio\" name=\"emailonpm\" $emailonpmnotchecked value=\"no\"> no\n\t</normalfont></td>\n</tr>\n<tr>\n\t<td bgcolor=\"{firstaltcolor}\"><normalfont><b>Pop up a box when you receive a Private Message?</b></normalfont><br>\n\t<smallfont>This will pop up a small warning box when you receive a Private Message asking whether you want to view the message.</smallfont></td>\n\t<td bgcolor=\"{firstaltcolor}\"><normalfont>\n\t\t<input type=\"radio\" name=\"pmpopup\" $pmpopupchecked value=\"yes\"> yes\n\t\t<input type=\"radio\" name=\"pmpopup\" $pmpopupnotchecked value=\"no\"> no\n\t</normalfont></td>\n</tr>\n<!-- *** -->\n<tr>\n\t<td bgcolor=\"{tableheadbgcolor}\" colspan=\"2\"><normalfont color=\"{tableheadtextcolor}\"><b>Thread View Options</b></normalfont></td>\n</tr>\n<!-- *** -->\n<tr>\n\t<td bgcolor=\"{secondaltcolor}\"><normalfont><b>Show user\'s signatures in their posts?</b></normalfont></td>\n\t<td bgcolor=\"{secondaltcolor}\"><normalfont>\n\t\t<input type=\"radio\" name=\"showsignatures\" value=\"yes\" $showsignatureschecked> yes\n\t\t<input type=\"radio\" name=\"showsignatures\" value=\"no\" $showsignaturesnotchecked> no\n\t</normalfont></td>\n</tr>\n<tr>\n\t<td bgcolor=\"{firstaltcolor}\"><normalfont><b>Show user\'s avatar in their posts?</b></normalfont></td>\n\t<td bgcolor=\"{firstaltcolor}\"><normalfont>\n\t\t<input type=\"radio\" name=\"showavatars\" value=\"yes\" $showavatarschecked> yes\n\t\t<input type=\"radio\" name=\"showavatars\" value=\"no\" $showavatarsnotchecked> no\n\t</normalfont></td>\n</tr>\n<tr>\n\t<td bgcolor=\"{secondaltcolor}\"><normalfont><b>Show image attachments and [img] code in Posts?</b></normalfont><br>\n\t<smallfont>Selecting no will show a hyperlink instead.</smallfont></td>\n\t<td bgcolor=\"{secondaltcolor}\"><normalfont>\n\t\t<input type=\"radio\" name=\"showimages\" value=\"yes\" $showimageschecked> yes\n\t\t<input type=\"radio\" name=\"showimages\" value=\"no\" $showimagesnotchecked> no\n\t</normalfont></td>\n</tr>\n<tr>\n\t<td bgcolor=\"{firstaltcolor}\"><normalfont><b>Default Thread View:</b></normalfont></td>\n\t<td bgcolor=\"{firstaltcolor}\"><smallfont>\n\t<select name=\"prunedays\">\n\t\t<option value=\"-1\" $daysdefaultselected>Use forum default</option>\n\t\t<option value=\"1\" $days1selected>Show threads from last day</option>\n\t\t<option value=\"2\" $days2selected>Show threads from last 2 days</option>\n\t\t<option value=\"5\" $days5selected>Show threads from last 5 days</option>\n\t\t<option value=\"10\" $days10selected>Show threads from last 10 days</option>\n\t\t<option value=\"20\" $days20selected>Show threads from last 20 days</option>\n\t\t<option value=\"30\" $days30selected>Show threads from last 30 days</option>\n\t\t<option value=\"45\" $days45selected>Show threads from last 45 days</option>\n\t\t<option value=\"60\" $days60selected>Show threads from last 60 days</option>\n\t\t<option value=\"75\" $days75selected>Show threads from last 75 days</option>\n\t\t<option value=\"100\" $days100selected>Show threads from last 100 days</option>\n\t\t<option value=\"365\" $days365selected>Show threads from the last year</option>\n\t\t<option value=\"1000\" $days1000selected>Show all threads</option>\n\t</select></smallfont></td>\n</tr>\n<tr>\n\t<td bgcolor=\"{secondaltcolor}\"><normalfont><b>Default Posts per Thread:</b></normalfont><br>\n\t<smallfont>Number of posts that are shown on one page of a thread.</smallfont></td>\n\t<td bgcolor=\"{secondaltcolor}\"><smallfont>\n\t<select name=\"umaxposts\">\n\t\t<option value=\"-1\" $postsdefaultselected>Use forum default</option>\n\t\t$maxpostsoptions\n\t</select></smallfont></td>\n</tr>\n<!-- *** -->\n<tr>\n\t<td bgcolor=\"{tableheadbgcolor}\" colspan=\"2\"><normalfont color=\"{tableheadtextcolor}\"><b>Date &amp; Time Options</b></normalfont></td>\n</tr>\n<!-- *** -->\n<tr>\n\t<td bgcolor=\"{secondaltcolor}\"><normalfont><b>Start of the week</b></normalfont><br>\n\t<smallfont>Please choose the day of the week on which weeks start in your culture.\n\tThis is so your calendar appears correct to you.</smallfont></td>\n\t<td bgcolor=\"{secondaltcolor}\"><smallfont>\n\t<select name=\"startofweek\">\n\t\t<option value=\"1\" $day1selected>Sunday</option>\n\t\t<option value=\"2\" $day2selected>Monday</option>\n\t\t<option value=\"3\" $day3selected>Tuesday</option>\n\t\t<option value=\"4\" $day4selected>Wednesday</option>\n\t\t<option value=\"5\" $day5selected>Thursday</option>\n\t\t<option value=\"6\" $day6selected>Friday</option>\n\t\t<option value=\"7\" $day7selected>Saturday</option>\n\t</select></smallfont></td>\n</tr>\n<tr>\n\t<td bgcolor=\"{firstaltcolor}\" colspan=\"2\"><normalfont><b>Time offset:</b><br>\n\t<smallfont>$timezone Please select your time zone from the list:<br>\n\t<se