INSERT INTO template VALUES("262","-1","modifypassword","{htmldoctype}\n<html>\n<head>\n<title>$bbtitle - Change password</title>\n$headinclude\n</head>\n<body>\n$header\n\n<table cellpadding=\"2\" cellspacing=\"0\" border=\"0\" width=\"{contenttablewidth}\" {tableinvisibleextra} align=\"center\">\n<tr>\n\t<td width=\"100%\"><img src=\"{imagesfolder}/vb_bullet.gif\" align=\"middle\" alt=\"vBulletin\">\n\t<normalfont><b><a href=\"index.php?s=$session[sessionhash]\">$bbtitle</a> &gt;\n\t<a href=\"usercp.php?s=$session[sessionhash]\">User Control Panel For $bbuserinfo[username]</a> &gt; Edit Password</b></normalfont></td>\n</tr>\n</table>\n\n<br>\n$cpnav\n<br>\n\n<form action=\"member.php\" method=\"post\">\n<input type=\"hidden\" name=\"s\" value=\"$session[sessionhash]\">\n\n<table cellpadding=\"{tableouterborderwidth}\" cellspacing=\"0\" border=\"0\" bgcolor=\"{tablebordercolor}\" {tableouterextra} width=\"{contenttablewidth}\" align=\"center\"><tr><td>\n<table cellpadding=\"4\" cellspacing=\"{tableinnerborderwidth}\" border=\"0\" {tableinnerextra} width=\"100%\">\n<tr>\n\t<td bgcolor=\"{tableheadbgcolor}\" colspan=\"2\"><normalfont color=\"{tableheadtextcolor}\" class=\"thtcolor\"><b>Edit Password - $bbuserinfo[username]</b></normalfont></td>\n</tr>\n<tr>\n\t<td bgcolor=\"{firstaltcolor}\"><normalfont><b>Present Password:</b></normalfont></td>\n\t<td bgcolor=\"{firstaltcolor}\"><normalfont><input type=\"password\" class=\"bginput\" name=\"currentpassword\" size=\"30\" maxlength=\"50\" tabindex=\"1\"></normalfont>\n\t<smallfont><a href=\"member.php?s=$session[sessionhash]&action=lostpw\">Forgotten your password?</a></smallfont></td>\n</tr>\n<tr>\n\t<td bgcolor=\"{secondaltcolor}\"><normalfont><b>New Password:</b></normalfont></td>\n\t<td bgcolor=\"{secondaltcolor}\"><normalfont><input type=\"password\" class=\"bginput\" name=\"newpassword\" size=\"30\" maxlength=\"50\" tabindex=\"2\"></normalfont></td>\n</tr>\n<tr>\n\t<td bgcolor=\"{secondaltcolor}\"><normalfont><b>