INSERT INTO template VALUES("269","-1","newpoll","{htmldoctype}\n<html>\n<head>\n<title>$bbtitle - Post New Poll</title>\n$headinclude\n</head>\n<body>\n$header\n\n<!-- breadcrumb -->\n<table cellpadding=\"2\" cellspacing=\"0\" border=\"0\" width=\"{contenttablewidth}\" {tableinvisibleextra} align=\"center\">\n<tr>\n\t<td width=\"100%\">$navbar</td>\n</tr>\n</table>\n<!-- /breadcrumb -->\n\n$pollpreview\n\n<br>\n\n<table cellpadding=\"2\" cellspacing=\"0\" border=\"0\" width=\"{contenttablewidth}\" {tableinvisibleextra} align=\"center\">\n<tr>\n\t<td><smallfont><b>Forum Rules:</b><br>$forumrules</smallfont></td>\n</tr>\n</table>\n\n<br>\n\n<form action=\"poll.php\" method=\"post\">\n<input type=\"hidden\" name=\"s\" value=\"$session[sessionhash]\">\n\n<table cellpadding=\"{tableouterborderwidth}\" cellspacing=\"0\" border=\"0\" bgcolor=\"{tablebordercolor}\" {tableouterextra} width=\"{contenttablewidth}\" align=\"center\"><tr><td>\n<table cellpadding=\"4\" cellspacing=\"{tableinnerborderwidth}\" border=\"0\" {tableinnerextra} width=\"100%\">\n<tr>\n\t<td bgcolor=\"{tableheadbgcolor}\" colspan=\"2\"><normalfont color=\"{tableheadtextcolor}\" class=\"thtcolor\"><b>Post New Poll</b></normalfont></td>\n</tr>\n$logincode\n<tr>\n\t<td bgcolor=\"{secondaltcolor}\"><normalfont><b>Question:</b></normalfont></td>\n\t<td bgcolor=\"{secondaltcolor}\"><normalfont><input type=\"text\" class=\"bginput\" name=\"question\" value=\"$question\" size=\"40\" maxlength=\"85\"></normalfont></td>\n</tr>\n<tr>\n\t<td bgcolor=\"{firstaltcolor}\"><normalfont><b>Number of options</b></normalfont><br>\n\t<smallfont>Maximum: $maxpolloptions</smallfont></td>\n\t<td bgcolor=\"{firstaltcolor}\"><normalfont><input type=\"text\" class=\"bginput\" name=\"polloptions\" value=\"$polloptions\" size=\"10\">\n\t&nbsp; &nbsp;\n\t<input type=\"submit\" class=\"bginput\" name=\"updatenumber\" accesskey=\"u\" value=\"Update options\"></normalfont></td>\n</tr>\n<tr>\n\t<td bgcolor=\"{secondaltcolor}\" valign=\"