INSERT INTO template VALUES("401","-1","reportbadpost","{htmldoctype}\n<html>\n<head>\n<title>$bbtitle - Report a bad post</title>\n$headinclude\n</head>\n<body>\n$header\n\n<br>\n\n<form action=\"report.php\" method=\"post\">\n<input type=\"hidden\" name=\"s\" value=\"$session[sessionhash]\">\n\n<table cellpadding=\"{tableouterborderwidth}\" cellspacing=\"0\" border=\"0\" bgcolor=\"{tablebordercolor}\" {tableouterextra} width=\"{contenttablewidth}\" align=\"center\"><tr><td>\n<table cellpadding=\"4\" cellspacing=\"{tableinnerborderwidth}\" border=\"0\" {tableinnerextra} width=\"100%\">\n<tr>\n\t<td bgcolor=\"{tableheadbgcolor}\" colspan=\"2\"><normalfont color=\"{tableheadtextcolor}\" class=\"thtcolor\"><b>Report this Post to a Moderator</b></normalfont></td>\n</tr>\n$logincode\n<tr>\n\t<td bgcolor=\"{secondaltcolor}\" valign=\"top\"><normalfont><b>Reason:</b></normalfont><br><br>\n\t<b><normalfont color=\"red\">This is ONLY to be used to report spam, advertising messages, and problematic (harassment, fighting, or rude) posts.</normalfont></b></td>\n\t<td bgcolor=\"{secondaltcolor}\"><textarea name=\"reason\" rows=\"6\" cols=\"45\"></textarea></td>\n</tr>\n</table>\n</td></tr></table>\n\n<br>\n\n<table cellpadding=\"2\" cellspacing=\"0\" border=\"0\" width=\"{contenttablewidth}\" {tableinvisibleextra} align=\"center\">\n<tr>\n\t<td align=\"center\"><p align=\"center\"><normalfont>\n\t<input type=\"submit\" class=\"bginput\" name=\"SUBMIT\" value=\"Send Report Now!\">\n\t<input type=\"hidden\" name=\"postid\" value=\"$postid\">\n\t<input type=\"hidden\" name=\"action\" value=\"sendemail\">\n\t<input type=\"hidden\" name=\"url\" value=\"$url\">\n\t</normalfont></td>\n</tr>\n</table>\n\n</form>\n\n$footer\n\n</body>\n</html>");
INSERT INTO template VALUES("402","-1","searchintro","{htmldoctype}\n<html>\n<head><title>$bbtitle Search Page</title>\n$headinclude\n</head>\n<body>\n$header\n\n<!-- breadcrumb -->\n<table cellpadding=\"2\" cellspacing=\"0\" border=\"0\" width=\"{contenttablewidth}\" {tableinvisibleextra} align=\"center\">\n<tr>\n\t<td width=\"100%\">$navbar</td>\n</tr>\n</table>\n<!-- /breadcrumb -->\n\n<br>\n\n<form action=\"search.php\" method=\"post\">\n<input type=\"hidden\" name=\"s\" value=\"$session[sessionhash]\">\n\n<table cellpadding=\"{tableouterborderwidth}\" cellspacing=\"0\" border=\"0\" bgcolor=\"{tablebordercolor}\" {tableouterextra} width=\"{contenttablewidth}\" align=\"center\"><tr><td>\n<table cellpadding=\"4\" cellspacing=\"{tableinnerborderwidth}\" border=\"0\" {tableinnerextra} width=\"100%\">\n<tr>\n\t<td bgcolor=\"{categorybackcolor}\" colspan=\"2\"><normalfont color=\"{categoryfontcolor}\"><b>\n\t$bbtitle Search Engine\n\t</b></normalfont></td>\n</tr>\n<tr>\n\t<td bgcolor=\"{tableheadbgcolor}\"><smallfont color=\"{tableheadtextcolor}\"><b>Search By Keyword</b></smallfont></td>\n\t<td bgcolor=\"{tableheadbgcolor}\"><smallfont color=\"{tableheadtextcolor}\"><b>Search By User Name</b></smallfont></td>\n</tr>\n<tr valign=\"top\">\n\t<td bgcolor=\"{firstaltcolor}\"><smallfont>\n\t<br><input type=\"text\" class=\"bginput\" name=\"query\" size=\"35\">\n\t<br><br>\n\t<b>Basic query:</b>\n\tseparate your search terms with spaces.<br>\n\t<br>\n\t<b>Advanced query:</b>\n\tJoin words with AND, OR and NOT to control your search in more detail.<br>\n\tAdd asterisks (*) to use wild cards in your search\n\t(<i>*bullet*</i> matches <i>vBulletin</i> etc.)\n\t</smallfont></td>\n\t<!-- *** -->\n\t<td bgcolor=\"{firstaltcolor}\"><p><smallfont>\n\t<br><input type=\"text\" class=\"bginput\" name=\"searchuser\" size=\"25\" maxlength=\"25\">\n\t<br><br>\n\t<input type=\"radio\" name=\"exactname\" value=\"yes\" checked> Match exact name<br>\n\t<input type=\"radio\" name=\"exactname