INSERT INTO template VALUES("1999","1","registeradult","{htmldoctype}\r\n<html>\r\n<head>\r\n<title>$bbtitle Registrierung</title>\r\n$headinclude\r\n</head>\r\n<body>\r\n\r\n$header\r\n\r\n<br>\r\n\r\n<table cellpadding=\"2\" cellspacing=\"0\" border=\"0\" width=\"{contenttablewidth}\" {tableinvisibleextra} align=\"center\">\r\n<tr>\r\n        <td><normalfont>Um in den $bbtitle Foren Beiträge schreiben zu dürfen, musst Du Dich zuerst registrieren.</normalfont></td>\r\n</tr>\r\n</table>\r\n\r\n<br>\r\n\r\n<form action=\"register.php\" method=\"post\">\r\n<input type=\"hidden\" name=\"s\" value=\"$session[sessionhash]\">\r\n\r\n<table cellpadding=\"{tableouterborderwidth}\" cellspacing=\"0\" border=\"0\" bgcolor=\"{tablebordercolor}\" {tableouterextra} width=\"{contenttablewidth}\" align=\"center\"><tr><td>\r\n<table cellpadding=\"4\" cellspacing=\"{tableinnerborderwidth}\" border=\"0\" {tableinnerextra} width=\"100%\">\r\n<!-- *** -->\r\n<tr>\r\n        <td bgcolor=\"{tableheadbgcolor}\" colspan=\"2\"><normalfont color=\"{tableheadtextcolor}\" class=\"thtcolor\"><b>Registrierung: Erforderliche Informationen</b></normalfont>\r\n        <smallfont color=\"{tableheadtextcolor}\" class=\"thtcolor\">Alle Felder müssen ausgefüllt werden.  Achte bei dem Kennwort auf Gross-/Kleinschreibung.</smallfont></td>\r\n</tr>\r\n<!-- *** -->\r\n<tr>\r\n        <td bgcolor=\"{secondaltcolor}\"><normalfont><b>Benutzername:</b></normalfont></td>\r\n        <td bgcolor=\"{secondaltcolor}\"><normalfont><input type=\"text\" class=\"bginput\" name=\"username\" size=\"25\" maxlength=\"$maxuserlength\"></normalfont></td>\r\n</tr>\r\n<tr>\r\n        <td bgcolor=\"{firstaltcolor}\"><normalfont><b>Kennwort:</b></normalfont></td>\r\n        <td bgcolor=\"{firstaltcolor}\"><normalfont><input type=\"password\" class=\"bginput\" name=\"password\" size=\"25\" maxlength=\"15\"></normalfont></td>\r\n</tr>\r\n<tr>\r\n        <td bgcolor=\"{firstaltcolor}\"><normalfont><b>Kennwort wiederholen:</b></normalfont></td>\r\n        <td bgcolor=\"{firstaltcolor}\"><input type=\"password\" class=\"bginput\" name=\"passwordconfirm\" size=\"25\" maxlength=\"15\"></td>\r\n</tr>\r\n<tr>\r\n        <td bgcolor=\"{secondaltcolor}\"><normalfont><b>eMail-Adresse:</b></normalfont><br>\r\n        <smallfont>Bitte gib eine gültige eMail-Adresse an. Du kannst weiter unten einstellen, ob sie für andere Benutzer sichtbar ist oder nicht.</smallfont></td>\r\n        <td bgcolor=\"{secondaltcolor}\"><normalfont><input type=\"text\" class=\"bginput\" name=\"email\" size=\"25\" maxlength=\"50\"></normalfont></td>\r\n</tr>\r\n<tr>\r\n        <td bgcolor=\"{secondaltcolor}\"><normalfont><b>eMail-Adresse wiederholen:</b></normalfont></td>\r\n        <td bgcolor=\"{secondaltcolor}\"><normalfont><input type=\"text\" class=\"bginput\" name=\"emailconfirm\" size=\"25\" maxlength=\"50\"></normalfont></td>\r\n</tr>\r\n<!-- *** -->\r\n$imageregbit\r\n<!-- *** -->\r\n$customfields_required\r\n<!-- *** -->\r\n<tr>\r\n        <td bgcolor=\"{tableheadbgcolor}\" colspan=\"2\"><normalfont color=\"{tableheadtextcolor}\" class=\"thtcolor\"><b>Freiwillige Informationen</b></normalfont>\r\n        <smallfont color=\"{tableheadtextcolor}\" class=\"thtcolor\">Alle folgenden Informationen können von anderen Nutzern des $bbtitle eingesehen werden.</smallfont></td>\r\n</tr>\r\n<!-- *** -->\r\n<tr>\r\n        <td bgcolor=\"{firstaltcolor}\"><normalfont><b>Homepage:</b></normalfont></td>\r\n        <td bgcolor=\"{firstaltcolor}\"><normalfont><input type=\"text\" class=\"bginput\" name=\"homepage\" value=\"http://\" size=\"25\" maxlength=\"100\"></normalfont></td>\r\n</tr>\r\n<tr>\r\n        <td bgcolor=\"{secondaltcolor}\"><normalfont><b>ICQ Nummer:</b></normalfont></td>\r\n        <td bgcolor=\"{secondaltcolor}\"><normalfont><input type=\"text\" class=\"bginput\" name=\"icq\" size=\"25\" maxlength=\"20\"></normalfont></td>\r\n</tr>\r\n<tr>\r\n        <td bgcolor=\"{firstaltcolor}\"><normalfont><b>AOL Instant Messenger Name:</b></normalfont></td>\r\n        <td bgcolor=\"{firstaltcolor}\"><normalfont><input type=\"text\" class=\"bginput\" name=\"aim\" size=\"25\" maxlength=\"20\"></normalfont></td>\r\n</tr>\r\n<tr>\r\n        <td bgcolor=\"{secondaltcolor}\"><normalfont><b>Yahoo Messenger Name:</b></normalfont></td>\r\n        <td bgcolor=\"{secondaltcolor}\"><normalfont><input type=\"text\" class=\"bginput\" name=\"yahoo\" size=\"25\" maxlength=\"20\"></normalfont></td>\r\n</tr>\r\n$referrer\r\n$birthday\r\n$customfields\r\n<!-- *** -->\r\n<tr>\r\n        <td bgcolor=\"{tableheadbgcolor}\" colspan=\"2\"><normalfont color=\"{tableheadtextcolor}\"><b>Einstellungen</b></normalfont></td>\r\n</tr>\r\n<!-- *** -->\r\n<tr>\r\n        <td bgcolor=\"{firstaltcolor}\" valign=\"top\"><normalfont><b>Signatur:</b></normalfont><br>\r\n        <smallfont>Die Signatur ist optional und wird am Ende Deiner Beiträge angezeigt (falls Du sie aktiviert hast).<br><br>\r\nHTML ist $htmlonoff<br>\r\n<A HREF=\"misc.php?s=$session[sessionhash]&action=bbcode\">vB Code</A> ist $bbcodeonoff<br>\r\n[img] Code ist $imgcodeonoff<br>\r\n<a href=\"misc.php?s=$session[sessionhash]&action=showsmilies\">Smilies</a> sind $smiliesonoff\r\n        </smallfont></td>\r\n        <td bgcolor=\"{firstaltcolor}\"><textarea name=\"signature\" rows=\"5\" cols=\"35\" wrap=\"physical\"></textarea></td>\r\n</tr>\r\n<tr>\r\n        <td bgcolor=\"{secondaltcolor}\"><normalfont><b>Dürfen Dir die Administratoren und Moderatoren eMails schicken?</b></normalfont></td>\r\n        <td bgcolor=\"{secondaltcolor}\"><normalfont>\r\n                <input type=\"radio\" name=\"allowmail\" value=\"yes\" checked> ja\r\n                <input type=\"radio\" name=\"allowmail\" value=\"no\"> nein\r\n        </normalfont></td>\r\n</tr>\r\n<tr>\r\n        <td bgcolor=\"{firstaltcolor}\"><normalfont><b>Unsichtbar sein?</b></normalfont><br>\r\n        <smallfont>Wählst Du hier ja, erscheint Dein Benutzername nicht in der Liste der Benutzer, die gerade im Board aktiv sind.</smallfont></td>\r\n        <td bgcolor=\"{firstaltcolor}\"><normalfont>\r\n                <input type=\"radio\" name=\"invisible\" value=\"yes\"> ja\r\n                <input type=\"radio\" name=\"invisible\" value=\"no\" checked> nein\r\n        </normalfont></td>\r\n</tr>\r\n<tr>\r\n        <td bgcolor=\"{secondaltcolor}\"><normalfont><b>eMail-Adresse verstecken?</b><br></normalfont>\r\n        <smallfont>Wählst Du hier ja, wird Deine eMail-Adresse in Deinem öffentlichen Profil nicht mehr angezeigt, so dass andere Nutzer des Boards sie nicht einsehen können.</smallfont></td>\r\n        <td bgcolor=\"{secondaltcolor}\"><normalfont>\r\n                <input type=\"radio\" name=\"showemail\" value=\"no\" > ja\r\n                <input type=\"radio\" name=\"showemail\" value=\"yes\" checked> nein\r\n        </normalfont></td>\r\n</tr>\r\n<tr>\r\n<td bgcolor=\"{firstaltcolor}\"><normalfont><b>Automatischer Login, wenn Du in dieses Forum zurückkehrst? (benutzt Cookies)</b></normalfont></td>\r\n<td bgcolor=\"{firstaltcolor}\"><normalfont>\r\n                <input type=\"radio\" name=\"cookieuser\" value=\"yes\" checked> ja\r\n                <input type=\"radio\" name=\"cookieuser\" value=\"no\"> nein\r\n        </normalfont></td>\r\n</tr>\r\n<tr>\r\n        <td bgcolor=\"{secondaltcolor}\"><normalfont><b>Cookies erlauben?</b></normalfont><br>\r\n        <smallfont>Wählst Du hier ja, werden Deine Benutzerdaten via Cookies für diese Session gespeichert. Wählst Du nein, werden die Benutzerdaten über die Links verschickt. (Das könnte Probleme geben, falls Du Links an andere Benutzer weitergibst, die denselben Proxy benutzen)</smallfont></td>\r\n        <td bgcolor=\"{secondaltcolor}\"><normalfont>\r\n                <input type=\"radio\" name=\"nosessionhash\" value=\"yes\" checked> ja\r\n                <input type=\"radio\" name=\"nosessionhash\" value=\"no\"> nein\r\n        </normalfont></td>\r\n</tr>\r\n<tr>\r\n        <td bgcolor=\"{firstaltcolor}\"><normalfont><b>Private Nachrichten aktivieren?</b></normalfont><br>\r\n        <smallfont>Wenn Du sie aktivierst, können Dir andere Benutzer Private Nachrichten (PNs) schicken.</smallfont></td>\r\n        <td bgcolor=\"{firstaltcolor}\"><normalfont>\r\n                <input type=\"radio\" name=\"receivepm\" value=\"yes\" checked> ja\r\n                <input type=\"radio\" name=\"receivepm\" value=\"no\"> nein\r\n        </normalfont></td>\r\n</tr>\r\n<tr>\r\n        <td bgcolor=\"{secondaltcolor}\"><normalfont><b>eMail Benachrichtigung für Private Nachrichten?</b></normalfont></td>\r\n        <td bgcolor=\"{secondaltcolor}\"><normalfont>\r\n                <input type=\"radio\" name=\"emailonpm\" value=\"yes\" checked> ja\r\n                <input type=\"radio\" name=\"emailonpm\"value=\"no\"> nein\r\n        </normalfont></td>\r\n</tr>\r\n<tr>\r\n        <td bgcolor=\"{firstaltcolor}\"><normalfont><b>Pop-up Benachrichtung für PN aktivieren?</b></normalfont><br>\r\n        <smallfont>Wenn Du auf das Board gehst und eine neue PN hast, öffnet sich eine Warnmeldung, dass Du eine bekommen hast!</smallfont></td>\r\n        <td bgcolor=\"{firstaltcolor}\"><normalfont>\r\n                <input type=\"radio\" name=\"pmpopup\" value=\"yes\" checked> ja\r\n                <input type=\"radio\" name=\"pmpopup\" value=\"no\"> nein\r\n        </normalfont></td>\r\n</tr>\r\n<tr>\r\n        <td bgcolor=\"{secondaltcolor}\"><normalfont><b>Themen der letzten X Tage anzeigen:</b></normalfont></td>\r\n        <td bgcolor=\"{secondaltcolor}\"><smallfont><select name=\"prunedays\">\r\n                <option value=\"-1\" selected>Standardeinstellung benutzen</option>\r\n                <option value=\"1\">Zeige Themen des letzten Tages</option>\r\n                <option value=\"2\">Zeige Themen der letzten 2 Tage</option>\r\n                <option value=\"5\">Zeige Themen der letzten 5 Tage</option>\r\n                <option value=\"10\">Zeige Themen der letzten 10 Tage<