INSERT INTO template VALUES("1846","1","newthread","{htmldoctype}\r\n<html>\r\n<head>\r\n<title>$bbtitle - Neues Thema erstellen</title>\r\n$headinclude\r\n<!--<script type=\"text/javascript\" language=\"javascript\" src=\"$bburl/spellcheck/spch.js\"></script>-->\r\n<script language=\"javascript\">\r\n<!--\r\nvar postmaxchars = $postmaxchars;\r\nfunction validate(theform) {\r\n        if (theform.message.value==\"\") {\r\n                alert(\"Bitte fülle Das Beitrags Feld aus.\");\r\n                return false; }\r\n        if (postmaxchars != 0) {\r\n                if (theform.message.value.length > $postmaxchars) {\r\n                        alert(\"Dein Beitrag ist zu lang.\\n\\nVerkürze Deinen Beitrag auf $postmaxchars Zeichen.\\nDein Beitrag ist \"+theform.message.value.length+\" Zeichen lang.\");\r\n                        return false; }\r\n                else { return true; }\r\n        } else { return true; }\r\n}\r\nfunction checklength(theform) {\r\n        if (postmaxchars != 0) { message = \"\\nDein Beitrag darf maximal $postmaxchars Zeichen lang sein.\"; }\r\n        else { message = \"\"; }\r\n        alert(\"Dein Beitrag ist \"+theform.message.value.length+\" Zeichen lang.\"+message);\r\n}\r\n//-->\r\n</script>\r\n</head>\r\n<body>\r\n$header\r\n\r\n<!-- breadcrumb -->\r\n<table cellpadding=\"2\" cellspacing=\"0\" border=\"0\" width=\"{contenttablewidth}\" {tableinvisibleextra} align=\"center\">\r\n<tr>\r\n        <td width=\"100%\">$navbar</td>\r\n</tr>\r\n</table>\r\n<!-- /breadcrumb -->\r\n\r\n$postpreview\r\n\r\n<br>\r\n\r\n<form $enctype action=\"newthread.php\" method=\"POST\" name=\"vbform\" onSubmit=\"return validate(this)\">\r\n<input type=\"hidden\" name=\"s\" value=\"$session[sessionhash]\">\r\n<input type=\"hidden\" name=\"forumid\" value=\"$forumid\">\r\n<input type=\"hidden\" name=\"action\" value=\"postthread\">\r\n\r\n<table cellpadding=\"{tableouterborderwidth}\" cellspacing=\"0\" border=\"0\" bgcolor=\"{tablebordercolor}\" {tableouterextra} width=\"{contenttablewidth}\" align=\"center\"><tr><td>\r\n<table cellpadding=\"4\" cellspacing=\"{tableinnerborderwidth}\" border=\"0\" {tableinnerextra} width=\"100%\">\r\n<tr>\r\n        <td bgcolor=\"{tableheadbgcolor}\" colspan=\"2\"><normalfont color=\"{tableheadtextcolor}\" class=\"thtcolor\"><b>Neues Thema erstellen</b></normalfont></td>\r\n</tr>\r\n$logincode\r\n<tr bgcolor=\"{secondaltcolor}\">\r\n        <td bgcolor=\"{secondaltcolor}\"><normalfont><b>Betreff:</b></normalfont></td>\r\n        <td bgcolor=\"{secondaltcolor}\"><normalfont><input type=\"text\" class=\"bginput\" name=\"subject\" value=\"$subject\" size=\"40\" maxlength=\"85\" tabindex=\"1\"></normalfont></td>\r\n</tr>\r\n$posticons\r\n$vbcode_buttons\r\n<tr>\r\n        <td bgcolor=\"{secondaltcolor}\" valign=\"top\" nowrap><normalfont><b>Dein Beitrag:</b></normalfont>\r\n                <p><smallfont>\r\n                Siehe Forum Regeln (unten),<br>\r\nwelche Textformatierungs-<br>\r\nCodes Du in Deinem Beitrag<br>\r\nbenutzen darfst.\r\n        </smallfont></p>\r\n$vbcode_smilies</td>\r\n        <td bgcolor=\"{secondaltcolor}\">\r\n                <table cellpadding=\"0\" cellspacing=\"0\" border=\"0\">\r\n                <tr valign=\"top\">\r\n                        <td><textarea name=\"message\" rows=\"20\" cols=\"$textareacols\" wrap=\"virtual\" tabindex=\"2\">$message</textarea><br>\r\n<smallfont><a href=\"javascript:checklength(document.vbform);\">[Beitragslänge prüfen]</a></smallfont></td>\r\n                </tr>\r\n                </table>\r\n        </td>\r\n</tr>\r\n<tr>\r\n        <td bgcolor=\"{firstaltcolor}\" valign=\"top\"><normalfont><b>Optionen:</b></normalfont></td>\r\n        <td bgcolor=\"{firstaltcolor}\" valign=\"top\"><smallfont>\r\n                <input type=\"checkbox\" name=\"parseurl\" value=\"yes\" $parseurlchecked> <b>URLs automatisch umwandeln:</b> fügt automatisch [url] und [/url] um Internetadressen ein.\r\n        <br><input type=\"checkbox\" name=\"email\" value=\"yes\" $emailchecked> <b>eMail Benachrichtigung:</b> sendet Dir eine eMail, wenn jemand auf Deinen Beitrag geantwortet hat. Nur für registrierte Benutzer.\r\n        $disablesmiliesoption\r\n        <br><input type=\"checkbox\" name=\"signature\" value=\"yes\" $signaturechecked> <b>Signatur anzeigen:</b> fügt Deine Signatur am Ende Deines Beitrags ein.  Nur für registrierte Benutzer.\r\n        </smallfont></td>\r\n</tr>\r\n$attachmentoption\r\n$postpolloption\r\n</table>\r\n</td></tr></table>\r\n\r\n<br>\r\n\r\n<table cellpadding=\"2\" cellspacing=\"0\" border=\"0\" width=\"{contenttablewidth}\" {tableinvisibleextra} align=\"center\">\r\n<tr>\r\n        <td align=\"center\"><normalfont>\r\n        <input type=\"submit\" class=\"bginput\" name=\"submit\" value=\"Thema erstellen\" accesskey=\"s\" tabindex=\"3\">\r\n        <input type=\"submit\" class=\"bginput\" name=\"preview\" value=\"Vorschau\" accesskey=\"p\" tabindex=\"4\">\r\n        <!--<input type=\"button\" class=\"bginput\" value=\"Rechtschreibung prüfen\" onclick=\"var f=document.forms[\'vbform\']; doSpell( \'$spellchecklang\', f.message, \'$bburl/spellcheck/sproxy.php\', true);\" tabindex=\"5\">-->\r\n        </normalfont></td>\r\n</tr>\r\n</table>\r\n\r\n</form>\r\n\r\n<table cellpadding=\"2\" cellspacing=\"0\" border=\"0\" width=\"{contenttablewidth}\" {tableinvisibleextra} align=\"center\">\r\n<tr>\r\n        <td>$forumrules</td>\r\n</tr>\r\n</table>\r\n\r\n$footer\r\n\r\n</body>\r\n</html>");
