INSERT INTO template VALUES("1608","1","editpoll","{htmldoctype}\r\n<html>\r\n<head>\r\n<title>$bbtitle - Umfrage bearbeiten</title>\r\n$headinclude\r\n</head>\r\n<body>\r\n$header\r\n\r\n<table cellpadding=\"2\" cellspacing=\"0\" border=\"0\" width=\"{contenttablewidth}\" {tableinvisibleextra} align=\"center\">\r\n<tr>\r\n        <td nowrap>$navbar\r\n                <p>$forumrules</p></td>\r\n</tr>\r\n</table>\r\n\r\n<br>\r\n\r\n<table cellpadding=\"{tableouterborderwidth}\" cellspacing=\"0\" border=\"0\" bgcolor=\"{tablebordercolor}\" {tableouterextra} width=\"{contenttablewidth}\" align=\"center\"><tr><td>\r\n<table cellpadding=\"4\" cellspacing=\"{tableinnerborderwidth}\" border=\"0\" {tableinnerextra} width=\"100%\">\r\n<form action=\"poll.php\" method=\"post\">\r\n<input type=\"hidden\" name=\"s\" value=\"$session[sessionhash]\">\r\n<tr>\r\n        <td bgcolor=\"{tableheadbgcolor}\" colspan=\"2\"><normalfont color=\"{tableheadtextcolor}\"><b>Umfrage bearbeiten</b></normalfont></td>\r\n</tr>\r\n<tr>\r\n        <td bgcolor=\"{firstaltcolor}\" colspan=\"2\"><normalfont><input type=\"checkbox\" name=\"closepoll\" value=\"yes\" $pollinfo[closed]><b>Schliessen?</b> Um diese Umfrage zu schliessen, markiere das Kästchen.<br>\r\n        <smallfont>Anmerkung: Wenn Du diese Umfrage schliesst, kann nicht mehr abgestimmt werden. Es können jedoch weiterhin Antworten zu diesem Thema verfasst werden.</smallfont></td>\r\n</tr>\r\n\r\n$logincode\r\n\r\n<tr>\r\n        <td bgcolor=\"{firstaltcolor}\" nowrap><normalfont><b>Frage:</b></normalfont></td>\r\n        <td bgcolor=\"{firstaltcolor}\"><normalfont><input type=\"text\" class=\"bginput\" name=\"pollquestion\" value=\"$pollinfo[question]\" size=\"40\" maxlength=\"85\"></normalfont></td>\r\n</tr>\r\n<tr>\r\n        <td bgcolor=\"{secondaltcolor}\" valign=\"top\" nowrap><normalfont><b>Zu bearbeitende Umfrage:</b></normalfont>\r\n        <p><br><smallfont>\r\n        Siehe Forum Regeln (oben),<br>\r\n        welche Texformatierungscodes<br>\r\n        Du in Deiner Umfrage<br>\r\n        benutzen darfst.</smallfont></p>\r\n        </td>\r\n        <td bgcolor=\"{secondaltcolor}\" valign=\"top\">\r\n                <table border=\"0\" cellpadding=\"0\">\r\n                $pollbits\r\n                <tr>\r\n                        <td><b><normalfont>Zusätzliche Felder:</normalfont></b></td>\r\n                </tr>\r\n                <tr>\r\n                        <td><normalfont>1. zusätzliche Antwortmöglichkeit:</normalfont></td>\r\n                        <td><normalfont><input name=\"options[]\" type=\"text\" class=\"bginput\" size=\"45\"></normalfont></td>\r\n                </tr>\r\n                <tr>\r\n                        <td><normalfont>2. zusätzliche Antwortmöglichkeit:</normalfont></td>\r\n                        <td><normalfont><input name=\"options[]\" type=\"text\" class=\"bginput\" size=\"45\"></normalfont></td>\r\n                </tr>\r\n                </table>\r\n        </td>\r\n</tr>\r\n<tr>\r\n        <td bgcolor=\"{firstaltcolor}\" nowrap><normalfont><b>Umfrage beenden:</b></normalfont></td>\r\n        <td bgcolor=\"{firstaltcolor}\"><normalfont><input type=\"text\" class=\"bginput\" name=\"timeout\" value=\"$pollinfo[timeout]\" size=\"10\" maxlength=\"85\"> Tage nach dem $pollinfo[postdate]</normalfont></td>\r\n</tr>\r\n</table>\r\n</td></tr></table>\r\n\r\n<br>\r\n\r\n<table cellpadding=\"2\" cellspacing=\"0\" border=\"0\" width=\"{contenttablewidth}\" {tableinvisibleextra} align=\"center\">\r\n<tr>\r\n        <td align=\"center\"><normalfont>\r\n<input type=\"hidden\" name=\"action\" value=\"updatepoll\">\r\n<input type=\"hidden\" name=\"pollid\" value=\"$pollid\">\r\n<input type=\"submit\" class=\"bginput\" accesskey=\"s\" value=\"Umfrage bearbeiten\">\r\n<input type=\"reset\" class=\"bginput\" value=\"Felder löschen\">\r\n        </normalfont></td>\r\n</tr>\r\n</table>\r\n\r\n</form>\r\n\r\n$footer\r\n\r\n</body>\r\n</html>");
INSERT INTO template VALUES("1609","1","editpost","{htmldoctype}\r\n<html>\r\n<head>\r\n<title>$bbtitle - Beitrag bearbeiten</title>\r\n$headinclude\r\n<!--<script type=\"text/javascript\" language=\"javascript\" src=\"$bburl/spellcheck/spch.js\"></script>-->\r\n<script language=\"javascript\">\r\n<!--\r\nvar postmaxchars = $postmaxchars;\r\nfunction validate(theform) {\r\n        if (theform.message.value==\"\") {\r\n                alert(\"Bitte fülle das Beitragsfeld aus.\");\r\n                return false; }\r\n        if (postmaxchars != 0) {\r\n                if (theform.message.value.length > $postmaxchars) {\r\n                        alert(\"Dein Beitrag ist zu lang.\\n\\nVerkürze Deinen Beitrag auf $postmaxchars Zeichen.\\nDein Beitrag ist \"+theform.message.value.length+\" Zeichen lang.\");\r\n                        return false; }\r\n                else { return true; }\r\n        } else { return true; }\r\n}\r\nfunction checklength(theform) {\r\n        if (postmaxchars != 0) { message = \"\\nDein Beitrag darf maximal $postmaxchars Zeichen lang sein.\"; }\r\n        else { message = \"\"; }\r\n        alert(\"Dein Beitrag ist \"+theform.message.value.length+\" Zeichen lang.\"+message);\r\n}\r\n//-->\r\n</script>\r\n</head>\r\n<body>\r\n$header\r\n\r\n<table cellpadding=\"2\" cellspacing=\"0\" border=\"0\" width=\"{contenttablewidth}\" {tableinvisibleextra} align=\"center\">\r\n<tr>\r\n        <td width=\"100%\">$navbar</td>\r\n</tr>\r\n</table>\r\n\r\n<br>\r\n\r\n<form action=\"editpost.php\" method=\"post\">\r\n<input type=\"hidden\" name=\"s\" value=\"$session[sessionhash]\">\r\n<input type=\"hidden\" name=\"postid\" value=\"$postid\">\r\n<input type=\"hidden\" name=\"action\" value=\"deletepost\">\r\n<table cellpadding=\"{tableouterborderwidth}\" cellspacing=\"0\" border=\"0\" bgcolor=\"{tablebordercolor}\" {tableouterextra} width=\"{contenttablewidth}\" align=\"center\"><tr><td>\r\n<table cellpadding=\"4\" cellspacing=\"{tableinnerborderwidth}\" border=\"0\" {tableinnerextra} width=\"100%\">\r\n<tr>\r\n        <td bgcolor=\"{tableheadbgcolor}\" colspan=\"2\"><normalfont color=\"{tableheadtextcolor}\"><b>Beitrag löschen</b></normalfont></td>\r\n</tr>\r\n<tr>\r\n        <td bgcolor=\"{firstaltcolor}\" colspan=\"2\">\r\n        <table border=\"0\" align=\"center\">\r\n        <tr valign=\"top\">\r\n                <td nowrap><normalfont><input type=\"checkbox\" name=\"deletepost\" value=\"yes\"> <b>Löschen?</b></normalfont></td>\r\n                <td width=\"100%\"><normalfont>Um diesen Beitrag zu löschen, markiere das Kästchen und klicke auf \"Beitrag löschen\".<br><smallfont>Anmerkung: Es wird das gesamte Thema gelöscht, wenn dieser Beitrag der erste in diesem Thema ist.</smallfont></normalfont></td>\r\n                <td nowrap><normalfont><input type=\"submit\" class=\"bginput\" name=\"SUBMIT\" value=\"Löschen\"></normalfont></td>\r\n        </tr>\r\n        </table>\r\n        </td>\r\n</tr>\r\n</table>\r\n</td></tr></table>\r\n</form>\r\n<form $enctype name=\"vbform\" action=\"editpost.php\" method=\"post\" onSubmit=\"return validate(this)\">\r\n<input type=\"hidden\" name=\"s\" value=\"$session[sessionhash]\">\r\n<input type=\"hidden\" name=\"action\" value=\"updatepost\">\r\n<input type=\"hidden\" name=\"postid\" value=\"$postid\">\r\n\r\n<table cellpadding=\"{tableouterborderwidth}\" cellspacing=\"0\" border=\"0\" bgcolor=\"{tablebordercolor}\" {tableouterextra} width=\"{contenttablewidth}\" align=\"center\"><tr><td>\r\n<table cellpadding=\"4\" cellspacing=\"{tableinnerborderwidth}\" border=\"0\" {tableinnerextra} width=\"100%\">\r\n<tr>\r\n        <td bgcolor=\"{tableheadbgcolor}\" colspan=\"2\"><normalfont color=\"{tableheadtextcolor}\"><b>Beitrag bearbeiten</b></normalfont></td>\r\n</tr>\r\n$logincode\r\n<tr>\r\n        <td bgcolor=\"{secondaltcolor}\" nowrap><normalfont><b>Betreff:</b></normalfont></td>\r\n        <td bgcolor=\"{secondaltcolor}\"><normalfont><input type=\"text\" class=\"bginput\" name=\"title\" value=\"$postinfo[title]\" size=\"40\" maxlength=\"85\" tabindex=\"1\"></normalfont></td>\r\n</tr>\r\n$posticons\r\n$vbcode_buttons\r\n<tr>\r\n        <td bgcolor=\"{secondaltcolor}\" valign=\"top\" nowrap><normalfont><b>Zu bearbeitender Beitrag:</b></normalfont>\r\n        <p><br><smallfont>\r\n        Siehe Forum Regeln (unten),<br>\r\n        welche Textformatierungscodes<br>\r\n        Du in Deinem Beitrag<br>\r\n        benutzen darfst.</smallfont></p>                $vbcode_smilies\r\n        </td>\r\n        <td bgcolor=\"{secondaltcolor}\"><smallfont>Original geschrieben von $postinfo[username] am $postinfo[postdate] um $postinfo[posttime]<br></smallfont>\r\n        <table cellpadding=\"0\" cellspacing=\"0\" border=\"0\">\r\n        <tr valign=\"top\">\r\n                <td><textarea name=\"message\" rows=\"20\" cols=\"$textareacols\" wrap=\"virtual\" tabindex=\"2\">$postinfo[message]</textarea><br>\r\n<smallfont><a href=\"javascript:checklength(document.vbform);\">[Beitragslänge prüfen]</a></smallfont></td>\r\n        </tr>\r\n        </table>\r\n        </td>\r\n</tr>\r\n<tr>\r\n        <td bgcolor=\"{firstaltcolor}\" valign=\"top\"><normalfont><b>Optionen:</b></normalfont></td>\r\n        <td bgcolor=\"{firstaltcolor}\" valign=\"top\"><smallfont>\r\n        <input type=\"checkbox\" name=\"parseurl\" value=\"yes\" $parseurlchecked> <b>URLs automatisch umwandeln:</b> fügt automatisch [url] und [/url] um Internetadressen ein.\r\n        <br><input type=\"checkbox\" name=\"email\" $emailchecked value=\"yes\"> <b>eMail Benachrichtigung:</b> Du bekommst eine eMail, wenn jemand auf diesen Beitrag antwortet. Nur für registrierte Bentuzer.\r\n        $disablesmiliesoption\r\n        <br><input type=\"checkbox\" name=\"signature\" $signaturechecked value=\"yes\"> <b>Signatur anzeigen:</b> fügt Deine Signatur unter Deinem Beitrag ein. Nur registrierte Benutzer können Signaturen haben.\r\n        </smallfont></td>\r\n</tr>\r\n$editattachment\r\n</table>\r\n</td></tr></table>\r\n\r\n<br>\r\n\r\n<table cellpadding=\"2\" cellspacing=\"0\" border=\"0\" width=\"{contenttablewidth}\" {tableinvisibleextra} align=\"center\">\r\n<tr>\r\n        <td align=\"center\"><normalfont>\r\n<input type=\"submit\" class=\"bginput\" name=\"submit\" accesskey=\"s\" value=\"Beitrag bearbeiten\" tabindex=\"3\">\r\n<!--<input type=\"button\" class=\"bginput\" value=\"Rechtschreibung prüfen\" onclick=\"var f=document.forms[\'vbform\']; doSpell( \'$spellchecklang\', f.message, \'$bburl/spellcheck/sproxy.php\', true);\">-->\r\n<input type=\"reset\" class=\"bginput\" value=\"Beitrag zurücksetzen\" tabindex=\"4\">\r\n        </normalfont></td>\r\n</tr>\r\n</table>\r\n\r\n</form>\r\n\r\n<table cellpadding=\"2\" cellspacing=\"0\" border=\"0\" width=\"{contenttablewidth}\" {tableinvisibleextra} align=\"center\">\r\n<tr>\r\n        <td>$forumrules</td>\r\n</tr>\r\n</table>\r\n$footer\r\n\r\n</body>\r\n</html>");
