INSERT INTO template VALUES("1581","1","calendar","{htmldoctype}\r\n<html>\r\n<head>\r\n<title>$bbtitle - Kalender $monthtext</title>\r\n$headinclude\r\n</head>\r\n<body>\r\n$header\r\n\r\n<table cellpadding=\"2\" cellspacing=\"0\" border=\"0\" width=\"{contenttablewidth}\" {tableinvisibleextra} align=\"center\">\r\n<tr>\r\n        <td width=\"100%\">$navbar</td>\r\n        <td nowrap align=\"right\">$addpub $addpriv</td>\r\n</tr>\r\n</table>\r\n\r\n<br>\r\n\r\n<table cellpadding=\"{tableouterborderwidth}\" cellspacing=\"0\" border=\"0\" bgcolor=\"{tablebordercolor}\" {tableouterextra} width=\"{contenttablewidth}\" align=\"center\"><tr><td>\r\n<table cellpadding=\"4\" cellspacing=\"{tableinnerborderwidth}\" border=\"0\" {tableinnerextra} width=\"100%\">\r\n<tr id=\"cat\">\r\n        <td bgcolor=\"{categorybackcolor}\" colspan=\"7\"><normalfont color=\"{categoryfontcolor}\"><b>$bbtitle Kalender</b></normalfont></td>\r\n</tr>\r\n$calendar_daynames\r\n<tr bgcolor=\"{pagebgcolor}\">\r\n$calendarbits\r\n</tr>\r\n</table>\r\n</td></tr></table>\r\n\r\n<br>\r\n\r\n<table cellpadding=\"{tableouterborderwidth}\" cellspacing=\"0\" border=\"0\" bgcolor=\"{tablebordercolor}\" {tableouterextra} width=\"{contenttablewidth}\" align=\"center\"><tr><td>\r\n<table cellpadding=\"4\" cellspacing=\"{tableinnerborderwidth}\" border=\"0\" {tableinnerextra} width=\"100%\">\r\n<form action=\"calendar.php\" method=\"post\"><input type=\"hidden\" name=\"s\" value=\"$session[sessionhash]\">\r\n<tr>\r\n        <td bgcolor=\"{secondaltcolor}\" align=\"center\">$addpub $addpriv</td>\r\n        <td bgcolor=\"{firstaltcolor}\" align=\"center\">\r\n        <select name=\"month\">\r\n                <option value=\"$NumMonth\">$NameMonth </option>\r\n                <option value=\"$NumMonth\">----------</option>\r\n                <option value=\"1\">$month1name</option>\r\n                <option value=\"2\">$month2name</option>\r\n                <option value=\"3\">$month3name</option>\r\n                <option value=\"4\">$month4name</option>\r\n                <option value=\"5\">$month5name</option>\r\n                <option value=\"6\">$month6name</option>\r\n                <option value=\"7\">$month7name</option>\r\n                <option value=\"8\">$month8name</option>\r\n                <option value=\"9\">$month9name</option>\r\n                <option value=\"10\">$month10name</option>\r\n                <option value=\"11\">$month11name</option>\r\n                <option value=\"12\">$month12name</option>\r\n        </select>\r\n        <select name=\"year\">\r\n                <option value=\"$NumYear\">$NumYear</option>\r\n                <option value=\"$NumYear\">----------</option>\r\n                $yearbits\r\n        </select>$gobutton\r\n        </td>\r\n        <td bgcolor=\"{secondaltcolor}\" align=\"right\"><smallfont>\r\n        <nobr><img src=\"{imagesfolder}/prev.gif\" alt=\"\" border=\"0\" align=\"absmiddle\">\r\n        <b>$premonth</b></nobr>\r\n        |\r\n        <nobr><b>$nextmonth</b>\r\n        <img src=\"{imagesfolder}/next.gif\" alt=\"\" border=\"0\" align=\"absmiddle\"></nobr>\r\n        </smallfont></td>\r\n</tr>\r\n</form>\r\n</table>\r\n</td></tr></table>\r\n\r\n\r\n$footer\r\n\r\n</body>\r\n</html>");
